    height: 75px !important
}

/* No more placeholders for posts from users you've blocked */
.blocked-post {
    display: none !important
}

/* Fixing the PM button */
img[src$="/static/layout/forum/forum-icon-message.png"] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://www1.flightrising.com/static/layout/small_message.png) no-repeat;
    width: 30px;
    height: 20px;
    padding-left: 30px;
}
#message-button {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://www1.flightrising.com/static/layout/small_message.png) no-repeat;
    width: 30px;
    height: 20px;
    padding-left: 30px;
}

/* Darkening things */
