    background-color: #c6c1b9 !important
}
.lighter {
    background-color: #c6c1b9 !important
}
.darker {
    background-color: #ded5c1 !important
}
#footer {
    background-color: #dad6c8
}
#postlist {
    margin-bottom: 35px !important
}
.post-frame,
.post:nth-child(2n) .post-frame {
