}
#specform div span {
    background: rgba(198, 193, 185, 0.8) !important
}
a {
    color: brown !important
}

/* Fixing issues caused by the global "a" rule */
.loginlinks {
    color: #e8cc9f !important
}
.navbar {
    color: #731d08 !important
}
.cluestat {
    color: black !important
}
.post-author-username {
    color: #d6d6d6 !important
}
.rotating-item a {
