    position: relative;
    right: 10px
}
.common-pagination .common-pagination-arrow-next img {
    position: relative;
    left: 10px
}

/* Removing that "new" image from the sidebar */
img[src="/images/icons/new_badge.png"],
img[src="/static/layout/festive/new.png"] {
