    background-image: url(https://orig00.deviantart.net/6bae/f/2018/223/2/5/ticker_3_by_nonyhost-dcjws0v.png) !important
}
#usertab {
    background-image: url(https://orig00.deviantart.net/1666/f/2018/223/d/4/usertab_better_by_nonyhost-dcjws35.png) !important
}

/* If you've un-squashed the top banner, you're going to want to swap out the above image for this one:
    		url(https://orig00.deviantart.net/5e24/f/2018/223/7/6/usertab_alternate_by_nonyhost-dcjws6s.png) */
#namespan {
    color: silver !important
}

/*Change this and the two below to whatever colour you like. I like lime. */
#namespan a span {
    color: silver !important
}
span[style="color:#731d08; font-size:15px; font-weight:bold;"] {
