    text-shadow: -2px -2px 0px rgba(64, 72, 63, 0.8), -2px -1px 0px rgba(50, 43, 41, 0.9), -2px 0px 0px rgba(50, 43, 41, 0.9), -2px 1px 0px rgba(50, 43, 41, 0.9), -2px 2px 0px rgba(36, 53, 46, 0.77), -1px -2px 0px rgba(50, 43, 41, 0.9), -1px -1px 0px rgba(50, 43, 41, 0.9), -1px 0px 0px rgba(50, 43, 41, 0.9), -1px 1px 0px rgba(50, 43, 41, 0.9), -1px 2px 0px rgba(50, 43, 41, 0.9), 0px -2px 0px rgba(50, 43, 41, 0.9), 0px -1px 0px rgba(50, 43, 41, 0.9), 0px 0px 0px rgba(50, 43, 41, 0.9), 0px 1px 0px rgba(50, 43, 41, 0.9), 0px 2px 0px rgb(20, 4, 0), 1px -2px 0px rgb(0, 0, 0), 1px -1px 0px rgba(50, 43, 41, 0.9), 1px 0px 0px rgb(0, 0, 0), 1px 1px 0px rgba(50, 43, 41, 0.9), 1px 2px 0px rgba(36, 16, 9, 0.9), 2px -2px 0px rgba(50, 43, 41, 0.9), 2px -1px 0px rgb(0, 0, 0), 2px 0px 0px rgb(0, 0, 0), 2px 1px 0px rgb(0, 0, 0), 2px 2px 0px rgba(44, 54, 48, 0.76)
