/* Coli stuff - now this was fun to figure out! */
#pl_content.fullscreen {
    background-color: #c6c1b9 !important
}
img[src$="http://flightrising.com/images/layout/nocanvas_top.png"] {
    display: none !important
}
img[src$="http://flightrising.com/images/layout/nocanvas_bottom.png"] {
    display: none !important
}

/* Dragon page */
span[style="display:inline-block; width:240px; border:1px solid #aaa79e; background-color:#F5F5F5;"] {
    background-color: #d1d1cf !important
}
span[style="display:inline-block; width:240px; border:1px solid #aaa79e; border-top:none; background-color:#EEE;"] {
    background-color: #dedcda !important
}
