a[onclick="topLink('food')"],
a[onclick="topLink('mats')"],
a[onclick="topLink('app')"],
a[onclick="topLink('fam')"],
a[onclick="topLink('battle')"],
a[onclick="topLink('skins')"],
a[onclick="topLink('specialty')"],
a[onclick="topLink('other')"] {
