}
div[style="width:175px; position:absolute; padding:5px; height:25px; border:1px solid #999999; text-align:left; background-color:#fff; -moz-border-radius:10px; -khtml-border-radius:10px; -webkit-border-radius:10px; border-radius:10px; left:200px; bottom:0px;"],
div[style="width:175px; position:absolute; padding:5px; height:25px; border:1px solid #999999; text-align:left; background-color:#fff; -moz-border-radius:10px; -khtml-border-radius:10px; -webkit-border-radius:10px; border-radius:10px; left:0px; bottom:0px;"] {
    background-color: #c6c1b9 !important
}

/* Baldwin stuff - All recipes fit without scrolling (on most monitors)!*/
.recipe {
    background-color: #ded5c1 !important
}
#baldwin-timer {
