css:
@-moz-document domain(dev.to) {
body{
    background: #141d26;
    color: #fff;
}
.single-article{
	background: #243447;
}
.top-bar {
	background: #243447;
}
.top-bar nav{
	background: #243447;
}
.nav-search-form__input{
	background: #141d26;
}
.nav-link {
	filter: invert(100%);
}
.content {
	color: #fff;
}
.home .articles-list .single-article {
	background: #243447;
}
.tag {
	color: #fff;
}
.on-page-nav-controls {
	filter: invert(100%);
}
.home .articles-list .article-engagement-count.bookmark-engage {
	background: #141d26;
}
.sidebar-profile-name, .sidebar-profile-username {
	color: #fff;
}
.home .side-bar .sidebar-nav .sidebar-profile-snapshot {
	background: #243447;
}
.home .side-bar .widget {
	background: #243447;
}
.home .side-bar .sidebar-nav .sidebar-nav-block .sidebar-nav-element a.sidebar-nav-link {
	background: #243447;
	color: #fff;
}
.home .side-bar .widget header a {
    color: #fff;
}
.home .side-bar .widget .widget-body .sidebar-sponsor .sponsor-tagline {
	color: #fff;
}
.home .side-bar .widget .widget-body .sidebar-sponsor .sponsor-tagline .sponsor-learn-more {
	color: #666;
}
.home .side-bar .widget header {
	color: #fff;
}
.home .side-bar .widget {
	color: #fff;
}
.home .side-bar .widget .social-icon {
	filter: invert(100%);
}
.home .side-bar .side-footer a {
	color: #fff;
}
.home .side-bar .sidebar-nav .sidebar-nav-block .sidebar-nav-subheader {
	color: #666;
}
.home .side-bar .widget .widget-body .widget-events-single-link {
	color: #fff;
}
.home .side-bar .widget .cta-button {
	border-radius: 20px;
    padding: 8px 10px;
    background: #243447;
    box-shadow: 0 0 2px white;
    text-align: center;
    color: #666;
}
.home .side-bar .widget-link-list__item a {
	color: #fff;
}
.home .side-bar .widget .widget-body .widget-podcast-ep a {
	color: #fff;
}
.home .articles-list .single-article.big-article .content-wrapper {
	color: #fff;
    background: #243447;
}
.home .articles-list .featured-tags .tag {
	color: #fff;
}
.top-bar nav .write {
	filter: invert(0%);
}
.stories-show {
    background: #141d26;
}
.container {
	background: #243447;
}
.container .body {
	background: #243447;
}
.container .body blockquote {
    border-left: calc(0.2vw + 2px) solid #141d26;
}
.container .about-the-author {
	border-radius: 20px;
    background: #141d26;
}
.container .about-the-author a {
    color: #fff;
}
.container .about-the-author .main-content .social {
    filter: invert(100%);
}
.comments-container form {
	background: #141d26;
}
.comments-container .icon-image {
    filter: invert(100%);
}
.comments-container .editor-image-upload .image-upload-button {
	background: #141d26;
}
.comments-container form .actions .comment-action-button {
	filter: invert(100%);
}
.single-comment-node .details {
    background: #141d26;
}
.container .body {
    color: #fff;
}
.single-comment-node .details .comment-username-inner {
	color: #fff;
}
.single-comment-node .body {
    padding-right: 2.5%;
    margin-bottom: -33px;
}
.single-comment-node .details .icon-img {
	filter: invert(100%);
}
.single-comment-node .reaction-button .reactions-count, .single-comment-node .dropbtn .reactions-count {
	color: #fff;
    background: #243447;
}
.single-comment-node .reaction-button.reacted .reactions-count, .single-comment-node .dropbtn.reacted .reactions-count {
	color: #fff;
    background: #243447;
}
.single-comment-node .reaction-button img, .single-comment-node .dropbtn img {
	filter: invert(100%);
}
.show-page-content-display a {
    color: #fff;
}
footer .container .inner-footer-container {
	background: #243447;
    color: #fff;
}
footer .container a {
	color: #fff;
}
.primary-sticky-nav .primary-sticky-nav-element {
	color: #fff;
    background: #243447;
}
.primary-sticky-nav .primary-sticky-nav-author a {
	color: #fff;
}
}
