css:
@-moz-document domain("eqinterface.com") {
div, body {
background-color: #262626;
color: #808080;
}
a:link {
color: #FFF;
}
a:visited {
color: #808080;
}
img {
    background: #FFF !important;
}
.inlineimg {
    background: #262626 !important;
}
.alt1, .alt1Active {
	background-color: #262626;
	color: #808080;
}
.alt2, .alt2Active {
	background-color: #262626;
	color: #808080;
}
.time {
color:#808080;
}
.smallfont i {
color: #808080 !important;
}
.smallfont {
background: #262626;
}
font {
color: #808080;
}
.thead {
background-color: #404040;
}
.tborder {
background-color: #262626;
border: 1px solid #808080;
}
.tcat {
background: #262626;
}
.vbmenu_control a:link {
	color: #FFFFFF;
}
.vbmenu_control {
	color: #FFFFFF;
    background: #808080;
}
.tfoot {
	color: #FFFFFF;
    background: #262626;
}
.panel {
	background-color: #262626;
	color: #808080;
	padding: 10px;
	border: 2px outset;
}
tbody td {
background: #262626;
}
}
