css:
@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/* left */
#header {
	border-right: none;
	background: #006666;
	color: #fff;
}
#header select,
#header a, 
#header .disabled a:hover {
	color: #fff;
}
#header .svg-icon.select-arrow,
#header .svg-icon.checked {
	fill: #fff;
}
.option.aligned >.select-resizer > select {
	background-color: #005b5b;
	border: 1px solid #005454;
}
.option.aligned >.select-resizer > select > optgroup,
.option.aligned >.select-resizer > select > option,
.select-resizer > select > option {
	background: #005454;
}
#search, 
#manage\.newUI\.sort,
#header input {
	background: #005b5b;
	color: #fff;
	border: 1px solid #005454;
}
::selection {
    color: #fff;
    background: #005b5b;
}
::-moz-selection {
    color: #fff;
    background: #005b5b;
}
::placeholder {
	color: #8abbb9;
}
button:disabled, 
select:disabled, 
option:disabled, 
select[disabled] > option {
	color: #8abbb9;
}
#header .svg-icon,
#header .svg-icon.info, 
#header .svg-icon.settings {
	fill: #ccc;
}
#header .svg-icon:hover,
#header .svg-icon.info:hover, 
#header .svg-icon.settings:hover {
	fill: #fff;
}
button {
	border: 1px solid #005454;
	color: #fff;
	background-color: #008888;
}
button:not(:disabled):hover,
button:active {
	background-color: #00a8a8;
	border-color: #005454;
}
/* right  */
body {
	background: #eee;
}
.newUI .entry.odd {
	background-color: rgba(0, 102, 102, 0.05);
}
}
