@-moz-document url-prefix("https://www.luogu.org/")
{
/*基础的一些乱搞设置*/
/*尽量不要去更改*/
  .a
  {
    transition: all 0.2s linear 0s;
    font-family: 隶书;
  }

  .a:hover
  {
    transition: all 0.2s linear 0s;
  }

  .lg-content
  {
    width: auto;
    overflow: hidden;
    height: 100%;
    min-height: 800px;
    vertical-align: top;
  }

  .lg-index-contest
  {
    transition: all 0.2s linear 0s;
    display: inline-block;
    margin: 3px;
    vertical-align: top;
    font-size: 1.3rem;
  }

  .lg-index-contest:hover
  {
    transition: all 0.2s linear 0s;
    box-shadow: 0 0 10px #0000ff;
  }

  .am-comment-hd
  {
    transition: all 0.2s linear 0s;
    background: rgba(248, 248, 248, 0.5);
    border-bottom: 1px solid #eee;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
  }

  .am-comment-hd:hover
  {
    transition: all 0.2s linear 0s;
    background: rgba(248, 248, 248, 0.8);
  }

  .am-comment-bd
  {
    transition: all 0.2s linear 0s;
    background: rgba(255, 255, 255, 0.5);
    font-size: 14px;
  }

  .am-comment-bd:hover
  {
    transition: all 0.2s linear 0s;
    background: rgba(255, 255, 255, 0.8);
  }

  .am-form textarea
  {
    transition: all 0.2s linear 0s;
    height: auto;
    background: transparent;
  }

  .am-form textarea:hover
  {
    transition: all 0.2s linear 0s;
    background: rgba(255, 255, 255, 0.8);
  }

  .am-g .am-g
  {
    transition: all 0.2s linear 0s;
    margin-left: -1rem;
    margin-right: -1rem;
    background: rgba(255, 255, 255, 0.49);
  }

  .am-g .am-g:hover
  {
    transition: all 0.2s linear 0s;
    background: rgba(255, 255, 255, 0.8);
  }

  .lg-content
  {
    width: auto;
    overflow: hidden;
    height: 100%;
    min-height: 800px;
    vertical-align: top;
  }

  .lg-summary
  {
    padding: 10px 10px;
    background: rgba(255,255,255,0.5);
  }

  pre
  {
    display: block;
    padding: 1rem;
    margin: 1rem 0;
    word-break: break-all;
    word-wrap: break-word;
    color: #555;
    border: 1px solid #dedede;
    border-radius: 0;
    box-shadow: inset 0 0 10px 3px #aaa;
    background: rgba(255, 255, 255, 0.50);
  }

  .hljs
  {
    background: rgba(255, 255, 255, 0.3);
  }

  .CodeMirror pre
  {
    box-shadow: none;
  }

  .am-pagination>li>a, .am-pagination>li>span
  {
    transition: all 0.2s linear 0s;
    background-color: rgba(255, 255, 255, 0.5);
    border-radius: 5px;
  }

  .am-pagination>li>a:focus, .am-pagination>li>a:hover, .am-pagination>li>span:focus, .am-pagination>li>span:hover
  {
    transition: all 0.2s linear 0s;
    background-color: rgba(255, 255, 255, 0.8);
  }
}

