css:
@-moz-document url-prefix("https://tweetdeck.twitter.com/") {
.js-app-columns,
html {
    background-color: rgb(20, 29, 38);
    color: rgb(255, 255, 255);
}

/* header*/
section.js-column > div > div > header {
    background-color: rgb(36, 52, 71);
}
.column-header,
.column-header-temp {
    color: #fff;
    background-color: #f5f8fa;
    ;
}
.column-header,
.column-header-temp {
    border-bottom: 1px solid #000
}

/* home */
.stream-item {
    border-bottom: 1px solid #000;
    background-color: #1b2836;
}
.rn-hzil2n {
    border-bottom-color: rgb(0, 0, 0);
}
.stream-item:hover {
    background-color: #141d26;
}
section.js-column > div > div > div > div {
    background-color: rgb(20, 29, 38);
}
.account-link {
    color: #fff
}
.quoted-tweet {
    border: 1px solid #000;
    color: #fff;
}

/* messages*/
.rpl textarea {
    border: 1px solid #000;
    background: rgb(20, 29, 38);
    box-shadow: inset 0 1px 1px rgba(20, 23, 26, .5);
}
.column-title-container {
    color: #fff
}

/* trending  */
.with-column-divider-bottom {
    border-bottom: 1px solid #000;
}
.column-message {
    background-color: #1b2836;
}
div.column-scroller:nth-child(3) {
    background-color: #1b2836;
}
.border-divider {
    border-bottom: 1px solid #000;
}
.bg-color-twitter-white,
.app-columns-container,
.app-content {
    background-color: #1b2836;
}

/* new tweet  */
.compose {
    background-color: rgb(20, 29, 38);
    color: #fff;
}
.compose-text-container {
    background-color: #1b2836;
    border: 1px solid #000;
}
.compose-text {
    background-color: #1b2836;
    color: #fff
}

/* Accounts*/
.drawer-header {
    border-bottom: 1px solid #000;
}
.bg-color-twitter-lightest-gray {
    background-color: #1b2836;
}
h1.column-title > span:nth-child(1) {
    color: #fff
}
.accordion .is-active,
.accordion .is-active .accordion-header,
.accordion .is-active .accordion-header:hover {
    color: #fff
}
.account-settings-bb {
    border-bottom: 1px solid #000
}
.join-team {
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
}
}
