css:
@-moz-document domain("finobe.com") {
body {
    background: url(https://web.archive.org/web/20110312012723im_/http://www.roblox.com/css/rbx2/images/bkg.jpg);
    background-repeat: repeat-x;
    background-color: rgba(0, 4, 67, 1.0);
}





#app {
    background-image: url(https://web.archive.org/web/20110312012735im_/http://www.roblox.com/css/rbx2/images/containerBkg_01.png);
    background-repeat: repeat;
    background-color: rgba(0, 4, 67, 1.0);
    width: 900px;
    padding-bottom: 20px;
    margin: auto; }





.row {
 margin-top: 10px;   
    margin-right: 0px;
    margin-left: 0px;
}





/* ---------------------------------------------------------------------------------------------------------------------------- */
/* Header */

.navbar {
    position: relative;
    margin: auto;
    padding: .0rem 0rem;
    width: 900px;
    height: 225px; }
.navbar-light.bg-faded {
    background-image: url(https://web.archive.org/web/20110312012725im_/http://www.roblox.com/css/rbx2/images/head_03.jpg); }

.navbar-brand {
    position: absolute;
    margin-top: -145px;
    margin-left: -15px;
    width: 900px;
    height: 259px;
    z-index: 3; }

/* Header */
/* ---------------------------------------------------------------------------------------------------------------------------- */





/* ---------------------------------------------------------------------------------------------------------------------------- */
/* Header Toolbar */

.navbar-expand-lg>.container {
    margin-top: 196px;
    width: 900px;
    height: 30px;
    background-image: url(https://web.archive.org/web/20110312013137im_/http://www.roblox.com/css/rbx2/images/topNav_blue.png); }

.navbar-brandimg {
    width: 22px;
    height: 22px; }

.nav-scroller-inner>a.nav-link, .navbar-light .navbar-nav .active>.nav-link, .navbar-light .navbar-nav .nav-link, .navbar-light .navbar-nav .nav-link.active, .navbar-light .navbar-nav .nav-link.show, .navbar-light .navbar-nav .show>.nav-link {
    color: white;
    font-family: Arial,Helvetica,sans-serif;
    font-size: 11pt;
    font-weight: bold; }

.navbar-light .navbar-brand, .navbar-light .navbar-light .navbar-light .navbar-nav .nav-link:focus, .navbar-light .navbar-nav .nav-link:hover {
    color: white;
    background-image: url('data:image/png;base64,');
    background-repeat: repeat-x;
    background-position: 0px 3px; }

/* Header Toolbar */
/* ---------------------------------------------------------------------------------------------------------------------------- */





/* ---------------------------------------------------------------------------------------------------------------------------- */
/* Secondary Toolbar */
.nav-scroller-inner>a.nav-link {
    position: relative;
    margin-top: -5px;
    color: white;
    font-family: Arial,Helvetica,sans-serif;
    font-size: 10pt;
    font-weight: normal; }

.nav-scroller {
    margin: auto;
    width: 900px;
    height: 30px;
    position: relative;
    -webkit-box-shadow: 0 0.0rem 0.0rem rgba(0,0,0,.0);
    box-shadow: 0 0.0rem 0.0rem rgba(0,0,0,.0); }

.nav-scroller.navbar-light.bg-faded {
        background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAYAAADtlXTHAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4ggBDzosA7nJKgAAAB1pVFh0Q29tbWVudAAAAAAAQ3JlYXRlZCB3aXRoIEdJTVBkLmUHAAAAKElEQVQI12NYtmzZfwYfH5//TP///2fATfz79494FrGKsRAMvLy8/wHK91bFeAXDcQAAAABJRU5ErkJggg=='); }
/* Secondary Toolbar */
/* ---------------------------------------------------------------------------------------------------------------------------- */





/* ---------------------------------------------------------------------------------------------------------------------------- */
/* Container */
.container {
    height: auto;
    width: 900px;
    padding-right: 5px;
    padding-left: 5px;
}

.mb-2, .my-2 {
    margin-bottom: 0px !important; }
.mt-2, .my-2 {
    margin-top: 0px !important; }
.mb-3, .my-3 {
    margin-bottom: 0rem!important; }
.mb-4, .my-4 {
    margin-bottom: 0rem!important; }

















.alert {
    width: 900px;
    margin: auto;
    border: 0;
    border-radius: 0;
    padding: .75rem 1.25rem;
}





















}

@-moz-document url-prefix("https://finobe.com/forum/") {
/* ---------------------------------------------------------------------------------------------------------------------------- */
/* Forum */

.forum-section-info {
    padding: 5px;
    margin-bottom: 8px;
    width: 890px;
    margin: auto;
    margin-top: 10px;
    background-image: url(https://web.archive.org/web/20120320185756im_/https://www.roblox.com/images/cssspecific/rbx2/standardBox_01_bkg.png);
    background-color: white;
    border: 1px solid #aaa; }

.col-lg-10 {
    background-image: url(https://web.archive.org/web/20120320185756im_/https://www.roblox.com/images/cssspecific/rbx2/standardBox_01_bkg.png);
    background-repeat: repeat-x;
    background-color: white;
    border: 1px solid #aaa; }

/* Forum */
/* ---------------------------------------------------------------------------------------------------------------------------- */
}

@-moz-document url-prefix("https://finobe.com/login") {
/* ---------------------------------------------------------------------------------------------------------------------------- */
/* Login */

.row {
 margin-top: 20px;   
    margin-right: 0px;
    margin-left: 0px; }

.alert-warning {
    width: 560px;
    margin: auto;
    background: #ffb300;
    color: #fff; }

.card {
    border-radius: 0rem;    
    border: 0px solid transparent;
    background-color: transparent; }

.card-body {
    border-radius: 0rem;
    background-image: url(https://web.archive.org/web/20110406064143im_/http://www.roblox.com/css/rbx2/images/standardBox_01_bkg.png);
    background-color: white;
    background-repeat: repeat-x;
    border: 1px solid #aaa; }

.card-header {
    color: white;
    font-size: 16px;
    
    background-color: rgba(0,0,0,0);
    border-bottom: 0px solid rgba(0,0,0,0);
    background: url(https://web.archive.org/web/20110406064242im_/http://www.roblox.com/css/rbx2/images/tab_black_33h_t2.png) no-repeat right top;
    width: 100%;
    height: 32px;
    padding-top: 5px;
    padding-left: 10px;
    font-family: Arial,Helvetica,sans-serif;
    font-weight: bold; }

/* Login */
/* ---------------------------------------------------------------------------------------------------------------------------- */
}
