css:
@-moz-document domain("optifine.net") {
@font-face {
        font-family: "Roboto";
        src: url(https://fonts.gstatic.com/s/roboto/v18/KFOmCnqEu92Fr1Mu72xKOzY.woff2);
}
* {
    font-family: 'Roboto', sans-serif !important;
}

.title {
    text-align: center;
}

.tableRoot {
    margin-top: 2%;
    width: 70%;
    border: none !important;
    background: rgba(38, 46, 46, 0.7);
    color: rgb(100,128,255);
}
.tableNav {
    background: rgba(100,128,255, 0.7);
}

.tableNav table{
    background: transparent !important;
    color: rgb(38,46,46) !important;
    padding-left: calc(100%*.34);
}

.tableNav a {
    color: rgb(38,46,46) !important;
}
body {
    color: rgb(100,128,255);
    background-image: url('data:image/png;base64,');
}

.title {
    background: transparent;
}

.footer {
    background: rgba(100,128,255, 0.7);
}

.footer table{
    background: transparent !important;
    color: rgb(38,46,46) !important;
}

.footer a {
    color: rgb(38,46,46) !important;
}

.downloadLineFirst {
    background: rgba(100,128,255, 0.7);
    color: rgb(38,46,46) !important;
}

.downloadLineFirst a {
    color: rgb(38,46,46) !important;
}

.downloads {
    padding-left: 10%;
}
}
