css:
@-moz-document domain("www.luogu.org") {
.lg-nav-logo
{
    display: block;
    margin-right: 10px;
    height: 50px;
    width: 160px;
    background: url('https://s1.ax1x.com/2018/08/15/P2oU7d.png') no-repeat center right;
    -webkit-background-size: 100px 32px;
    background-size: 100px 32px;
}
.lg-footer:after
{
    color: #999;
    font-weight: bold;
    content: "你们搞的这个蛤谷啊，EXCITED！";
    margin-left: 100px;
    font-size: 30px;
}
#submit:after
{
    content: "(损失1s HP)";
    margin-left: -5px;
}
#feed-submit:after
{
    content: "(损失1s HP)";
    margin-left: -14px;
}
#submitcode:after
{
    content: "(损失60s HP)";
    margin-left: 0px;
}
#submitpost:after
{
    content: "  膜一个！"
}
#submit-reply:after
{
    content: "  膜一个！"
}
#feed-more:after
{
    content: "长者的人生经验";
}
span[class="lg-punch-result lg-fg-red"]:before
{
    content: "§ 发财 §";
    font-size: 60px;
}
span[class="lg-punch-result lg-fg-red"]
{
    font-size: 0px !important;
}
span[class="lg-punch-result lg-fg-green"]:before
{
    content: "§ 养苟 §";
    font-size: 60px;
}
span[class="lg-punch-result lg-fg-green"]
{
    font-size: 0px !important;
}
span[class="lg-punch-result"]:before
{
    content: "§ naïve §";
    font-size: 60px;
}
span[class="lg-punch-result"]
{
    font-size: 0px !important;
}
h2[style="margin-bottom: 0"]:before
{
    content: "膜法师 ";
}
div[class="am-modal-bd"]:after
{
    content: "  懂了没有？识得唔识得？";
}
button[class="am-btn am-btn-danger"][name="recordsearch"]:after
{
    content: "查水表！";
    font-size: 16px;
}
button[class="am-btn am-btn-danger"][name="ratebtn"]:after
{
    content:"你们还要学习一个。"
}
button[class="am-btn am-btn-primary"][name="noratebtn"]:after
{
    content:"。我已经身经百战了。"
}
h1[style="color: #999;margin: 0"]
{
    font-size: 0px;
}
h1[style="color: #999;margin: 0"]:before
{
    color: #999;
    font-weight: bold;
    content: "在蛤谷，享受膜蛤的欢乐";
    font-size: 30px;
}
div[class="am-u-md-3 am-hide-sm"]:before
{
    display: block;
    height: 56px;
    width: 175px;
    content: " ";
    background: url('https://s1.ax1x.com/2018/08/15/P2oU7d.png') no-repeat center right;
    -webkit-background-size: 175px 56px;
    background-size: 175px 56px;
}
button[class="am-btn am-btn-sm am-btn-success"]:after
{
    content: "续掉！";
    font-size: 14px;
}
button[class="am-btn am-btn-sm am-btn-success"]
{
    height: 30px;
    width: 60px;
    font-size: 0px;
}
img[class="lg-inline-up"]
{
    width: 0px;
}
a[class="lg-header-li lg-md-hide"]:before
{
    height: 50px;
    content: "蛤谷网校";
    font-size: 12px;
}
a[class="lg-header-li lg-md-hide"]
{
    height: 50px;
    font-size: 0px;
}
a[href="/wiki/show?name=关于洛谷"]
{
    font-size: 0px;
}
a[href="/wiki/show?name=关于洛谷"]:before
{
    font-size: 14px;
    content: "关于蛤谷";
}
a[href="/wiki/show?name=帮助"]
{
    font-size: 0px;
}
a[href="/wiki/show?name=帮助"]:before
{
    font-size: 14px;
    content: "膜蛤帮助"
}
a[href="/wiki/show?name=洛谷社区规则"]
{
    font-size: 0px;
}
a[href="/wiki/show?name=洛谷社区规则"]:before
{
    font-size: 14px;
    content: "社区基本法"
}
a[href="/wiki/show?name=洛谷用户协议"]
{
    font-size: 0px;
}
a[href="/wiki/show?name=洛谷用户协议"]:before
{
    font-size: 14px;
    content: "膜法师守则"
}
a[href="/discuss/lists?forumname=miaomiaowu"]
{
    font-size: 0px;
}
a[href="/discuss/lists?forumname=miaomiaowu"]:before
{
    font-size: 14px;
    content: "处决名单"
}
a[href="/wiki/show?name=联系我们"]
{
    font-size: 0px;
}
a[href="/wiki/show?name=联系我们"]:before
{
    font-size: 14px;
    content: "续掉我们"
}
}
