css:
@-moz-document url-prefix("http://masayoshi4649pso2.gger.jp/") {
/*全体*/
 body {
    background-color: #000000;
}
a:link {
    color: #FFFFFF;
}
a:visited {
    color: #7e7cb4;
}
a:hover {
    text-decoration:underline;
}
body.page-article .article-title a:visited {
    color:#a0d8ef;
}
/* ブログのヘッダー、タイトル、説明
----------------------------------------------- */
 #blog-title {
    color: #FFFFFF;
}
#blog-title a {
    color: #FFFFFF;
}
#blog-eyecatch {
    background-color:#000000;
    box-shadow: inset 0 2px 10px rgba(0, 0, 0, 0.07);
}
#blog-header.show-image #blog-header-inner {
    background-image: linear-gradient(to bottom, #000000 0%, #111111 70%, #a0d8ef 100%);
}
#blog-header {
}
#blog-header-inner {
}
#blog-title {
    color: #FFFFFF;
}
#blog-title a {
    color: #FFFFFF;
}
/* ブログのフッター
----------------------------------------------- */
 #blog-footer {
    background-color: #000000;
    color:#a5a8aa;
}
#blog-footer a {
    color:#a5a8aa;
}
/* 記事本文
----------------------------------------------- */
 .article-body {
    color: #FFFFFF;
}
.article-body a {
    color: #777;
}
.article-body a:hover, .article-body a:visited {
    color: #999;
}
}
