css:
@-moz-document url-prefix("https://zh.moegirl.org/") {
/* Hide Left and Top Panel 隐藏左侧和上部的空白 */
#mw-panel {
    display: none!important;
}



/* Hide Header except Search and Personal Profile and Excute Relocalization 隐藏除搜索和用户档案外的其他元素并执行重定位 */
#right-navigation {
    float: right;
    margin-top: 2.5em;
    position: relative;
    top: 20px;
    left: -70px;
}

#left-navigation {
    float: right;
    margin-top: 2.5em;
    position: absolute;
    top: 15px;
    right: 400px;
}

#p-personal {
    float: right;
    margin-top: 2.5em;
    position: absolute;
    top: -40px;
    right: 58px;
}

#mw-head > div:not(#right-navigation):not(#p-personal):not(#left-navigation),
#right-navigation > div:not(#p-search):not(#p-cactions) {
    display: none;
}

#p-cactions {
    width: fit-content;
    float: right;
    margin-top: 2.5em;
    position: absolute;
    top: -45px;
    right: 470px;
}




/* Site Notice Rearrangement 公告重定位 */
#siteNotice {
    position: relative;
    text-align: left;
    margin: 0;
    z-index: 99999;
}



/*取消了捐款按钮……有时间再改吧啊啊啊啊啊我去这个坐标怎么变啊啊啊啊啊！！！！！！！！！！！*/
.please-donate {
    display: none;
}



/* Normal Links 一般超链接样式 */
a:link {
    color: #274787;
    text-decoration: none;
}

a:hover {
    color: #36c;
}

a:active {
    color: #40BCFF;
    outline: none;
}

a.new {
    color: #BA0000;
}



/* Heimu Links 黑幕超链接样式 */
.heimu a:link {
    color: #252525;
    text-decoration: none;
}

.heimu:hover {
    color: #FFFFFF;
}

.heimu:hover a:link {
    color: #93b7ff!important;
}

.heimu:hover a {
    color: #93b7ff!important;
}

.heimu:hover a:hover {
    color: #69b2fa!important;
}

.heimu a:link:hover {
    color: #69b2fa!important;
}

.heimu:hover a:link:visited {
    color: #3a5b9e;
}

.heimu a.new {
    color: #252525!important;
}

.heimu:hover a.new {
    color: #BA0000!important;
}



/* Sidebar Generation 侧边栏生成 */
#toc {
    font-family: Open Sans, sans-serif !important;
    /*border: 1px solid #EEE !important;*/
    border: none;
    /*border-radius: 2px !important;*/
    font-size: 12px;
    font-weight: 400;
    background: url(http://i.imgur.com/anSVcTO.png);
    /* make sidebar  */
    position: fixed !important;
    display: inline-block;
    top: 0;
    bottom: 0;
    left: 0;
    width: 200px;
    padding: 0.2em 0;
    margin: 0 .5em;
    overflow: auto;
}

#toc > ul {
    overflow-y: auto !important;
    /*word-break: break-all !important;*/
    /*height: 100% !important;*/
    overflow-x: hidden !important;
    background: #EDEDED url(http://i.imgur.com/anSVcTO.png) center center scroll;
    padding: 1em 0 5em;
    margin: auto 0 !important;
}

.toc ul ul {
    margin-left: 1em !important;
}

.toc a {
    display: block;
    padding: 0.25em 1em;
    border-radius: 2px;
    line-height: 1.25 !important;
    
}

body.skin-vector .tochide div.toc#toc {
    margin: 0;
    padding: 0;
    background: none;
    border-width: 0;
    width: 15%;
}

#mw-content-text > div > table.mw-collapsible.wikitable.tochide.nomobile > tbody > tr:nth-child(1) > td{
    width: 68px;
    text-align:left!important;
}

/* toc headers */
.toclevel-1 > a > .toctext {
    font-weight: 600;
    text-transform: uppercase;
    padding-top: 0.25em;
    display: inline-block;
}

.tocnumber {
    display: none;
}

.toc h2 {
    display: none !important;
}

.toccolours,
.thumb {
    border: 1px solid #EEE !important;
    /*    box-shadow: 0px 0px 3px 0 #999 !important; */
    border-radius: 2px !important;
}

div.toctitle {
    text-decoration: none !important;
}

span.toctoggle {
    display: none !important;
}



/* Sidebar Font 侧边栏字体 */
#toc a:link {
    color: #000 !important;
    text-decoration: none !important;
}

#toc a:visited {
    color: #000 !important;
}

#toc a:hover {
    color: #6B238E !important;
    text-decoration: none !important;
    background: rgba(0, 0, 0, 0.05);
}

#toc a:active {
    color: #000 !important;
    outline: none;
}



/* Background 背景 */
body.skin-vector {
    /*background: #373c38;*/
    /*background-image: linear-gradient(0deg, #196999, #6f94b6, #b0c1d3, #f0f0f0);*/
    background-image: linear-gradient(0deg, #aacfd0, #ededed, #f0f0f0, #ffffff);
    background-repeat: no-repeat;
    background-attachment: fixed;
    font-family: Arial, Tahoma, "Microsoft YaHei", \5fae\8f6f\96c5\9ed1, "Hiragino Sans GB", "Microsoft JhengHei", 微軟正黑體, sans-serif;
}



/* Title 题目 */
#firstHeading {
    font-weight: 500;
    font-size: 40px!important;
    color: black!important;
}



/* Section Headers 小结题目 */
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: Microsoft JhengHei UI !important;
    margin: 0.5em 0;
    color: #444;
}

h2 {
    font-size: 21px;
}

h3 {
    font-size: 18px;
    font-weight: 400;
}

h4 {
    font-size: 16px;
    font-weight: 400;
}

h5 {
    font-size: 1em;
    font-weight: 400;
}



/* Body 正文 */
body {
    font-size: inherit;
    background: #F3F3F2;
}

#bodyContent {
    font: 15px/1.5 Microsoft Yahei UI;
    font-weight: 500;
}



/* Body Background Rendering 正文背景渲染 */
#content.mw-body {
    position: relative;
    top: 0;
    bottom: 0;
    left: calc(200px + 1em);
    max-width: calc(100% - 315px);
    margin: 0;
    border: none !important;
    border-radius: 2px !important;
    padding: 0px 1.6em 2em 1em !important;
    background: none;
    color: #555;
}

#content ul,
#content ol {
    color: #666 !important;
    list-style-image: none;
    margin-bottom: 1em;
    margin-top: 0em;
}

#content table.wikitable {
    color: #666 !important;
    border: 1px solid #EEE !important;
}

table.wikitable > tr > th,
table.wikitable > tr > td,
table.wikitable > * > tr > th,
table.wikitable > * > tr > td {
    border: 1px solid #CCC !important;
}



/* Categories Optimization 分类优化 */
#mw-normal-catlinks {
    font-size: 0.8em;
}



/* Hide the Footer 隐藏底栏 */
#footer {
    display: none !important;
}
}
