@-moz-document domain("tweetdeck.twitter.com") {
/* version 1.18.2 */

    :root {
        --tweet-vertical-padding: 4px;
        --tweet-horizontal-padding: 6px;
        --tweet-avatar-size: calc(2rem + 6px);
        --tweet-avatar-margin-left: 0px;    /*not '0'*/
        --tweet-avatar-margin-right: 6px;
        --tweet-avatar-area-width: calc(var(--tweet-avatar-size) + var(--tweet-avatar-margin-left) + var(--tweet-avatar-margin-right));
        --bgcolor-light:            rgb(255, 255, 255);
        --bgcolor-light-90:        rgba(255, 255, 255, .90);
        --bgcolor-light-85:        rgba(255, 255, 255, .85);
        --bgcolor-light-emphasized: rgb(252, 252, 252);
        --bgcolor-dark:            rgb(21, 32, 43);
        --bgcolor-dark-90:        rgba(21, 32, 43, .90);
        --bgcolor-dark-85:        rgba(21, 32, 43, .85);
        --bgcolor-dark-emphasized: rgb(25, 36, 47);
        --bgcolor-og-dark:            rgb(34, 36, 38);
        --bgcolor-og-dark-90:        rgba(34, 36, 38, .90);
        --bgcolor-og-dark-85:        rgba(34, 36, 38, .85);
        --bgcolor-og-dark-emphasized: rgb(38, 40, 42);
    }

              .js-stream-item-content.js-show-detail,
    html.dark .js-stream-item-content.js-show-detail {
        padding: var(--tweet-vertical-padding) var(--tweet-horizontal-padding);
    }

    html.dark .activity-header.has-source-avatar {
        margin-top: 0;
    }

              .activity-header.has-source-avatar > .item-img,
    html.dark .activity-header.has-source-avatar > .item-img {
        width: var(--tweet-avatar-size);
    }

              .js-tweet:not(.tweet-detail),
    html.dark .js-tweet:not(.tweet-detail) {
        position: relative;
        padding-left: var(--tweet-avatar-area-width);
    }

              .js-inline-reply.item-box-full-bleed,
    html.dark .js-inline-reply.item-box-full-bleed {
        margin-bottom: calc(0px - var(--tweet-vertical-padding));
    }

              .js-tweet:not(.tweet-detail) .tweet-context,
    html.dark .js-tweet:not(.tweet-detail) .tweet-context {
        margin-left: calc(0px - var(--tweet-avatar-area-width));
        padding-bottom: 1px!important;
    }

              .js-tweet:not(.tweet-detail) .tweet-context .item-img,
    html.dark .js-tweet:not(.tweet-detail) .tweet-context .item-img {
        width: var(--tweet-avatar-size);
        margin-right: var(--tweet-avatar-margin-right);
    }

              .js-tweet:not(.tweet-detail) .item-img.tweet-img,
    html.dark .js-tweet:not(.tweet-detail) .item-img.tweet-img {
        margin-left: calc(0px - var(--tweet-avatar-margin-right) - var(--tweet-avatar-size));
    }

              .js-tweet:not(.tweet-detail) .avatar.tweet-avatar,
    html.dark .js-tweet:not(.tweet-detail) .avatar.tweet-avatar {
        width: var(--tweet-avatar-size);
        height: var(--tweet-avatar-size);
    }

              .js-tweet-body,
    html.dark .js-tweet-body {
        /*for flexbox. Without this value, tweet-body blocks of some tweets would penetrate their parent elements.*/
        min-width: 0;
        /*This prevents tweets with large media from collapsing; doesn't affect anything else*/
        min-height: 1em;
    }

              .other-replies,
    html.dark .other-replies {
        margin-top: -3px;
    }

    html.btd-on .btd__old_replies .js-stream-item:not(.btd-is-retweet) .js-tweet:not(.txt-mute) .tweet-body .other-replies::before {
        /*Modify the positions of reply symbols and their style*/
        position: absolute;
        width: calc(1em + 4px);
        height: calc(1em + 4px);
        text-align: center;
        left: calc(var(--tweet-avatar-size) + var(--tweet-avatar-margin-right) - 1em - 2px);
        top: calc(var(--tweet-avatar-size) - 1em + 4px);
        border-radius: calc(0.5em + 2px);
        background-color: var(--bgcolor-light);
    }
    html.btd-on.dark .btd__old_replies .js-stream-item:not(.btd-is-retweet) .js-tweet:not(.txt-mute) .tweet-body .other-replies::before {
        background-color: var(--bgcolor-dark);
    }
    html.btd-on.dark .btd__og_dark_theme.btd__old_replies .js-stream-item:not(.btd-is-retweet) .js-tweet:not(.txt-mute) .tweet-body .other-replies::before {
        background-color: var(--bgcolor-og-dark);
    }



    .js-tweet-body {
        overflow: hidden;
    }
    .js-card-container {
        position: relative;
    }
    .js-tweet-text + .js-card-container:empty:before,
    .js-quote-detail .js-quoted-tweet-text:after {
        display: inline-block;
        position: absolute;
        right: 0;
        content: "";
        width: 168px;
        background-color: var(--bgcolor-light);
        height: calc(1em + 4px);
    }
    .js-tweet-text + .js-card-container:empty:before{
        z-index: 4;
    }
    .js-quote-detail .js-quoted-tweet-text:after {
        z-index: -1;
    }
    html.dark .js-tweet-text + .js-card-container:empty:before,
    html.dark .js-quote-detail .js-quoted-tweet-text:after {
        background-color: var(--bgcolor-dark);
    }
    html.btd-on.dark .btd__og_dark_theme .js-tweet-text + .js-card-container:empty:before,
    html.btd-on.dark .btd__og_dark_theme .js-quoted-tweet-text:after {
        background-color: var(--bgcolor-og-dark);
    }
    .other-replies {
        position: relative;
        z-index: 5;
    }   
    html.btd-on .btd__old_replies .other-replies {
        position: static;
    } 



              .js-tweet-text,
              .js-quoted-tweet-text,
    html.dark .js-tweet-text,
    html.dark .js-quoted-tweet-text {
        position: relative;
        z-index: 5;
        display: inline;
    }
              .mdl .js-med-tweet .js-tweet-text,
              .mdl .js-med-tweet .js-quoted-tweet-text
    html.dark .mdl .js-med-tweet .js-tweet-text,
    html.dark .mdl .js-med-tweet .js-quoted-tweet-text {
        position: initial;
        z-index: auto;
        display: block;
    }

              .js-media-preview-container.margin-vm,
              .js-media.media-grid-container.media-size-medium.margin-vm,
    html.dark .js-media-preview-container.margin-vm,
    html.dark .js-media.media-grid-container.media-size-medium.margin-vm {
        /*for small or medium media, and their grid containers*/
        margin-top: 0!important;
        margin-bottom: 4px!important;
    }

              .js-media-preview-container.margin-t--20,
    html.dark .js-media-preview-container.margin-t--20 {
        /*for large media*/
        margin-top: 0px!important;
    }

              .js-media.item-box-full-bleed.margin-tm,
    html.dark .js-media.item-box-full-bleed.margin-tm {
        /*for large media or grid containers*/
        margin-top: 4px!important;
        margin-bottom: calc(0px - var(--tweet-vertical-padding));
    }

              .media-badge,
    html.dark .media-badge {
        /*for hidden media*/
        margin: 0 0 2px;
    }

    /* -- Fix and modify styles of sensitive media -- */
              .js-media .media-sensitive-detail,
    html.dark .js-media .media-sensitive-detail,
              .js-card-container .media-sensitive-detail,
    html.dark .js-card-container .media-sensitive-detail{
        /*for large media*/
        min-height: auto;
    }

              .js-media .js-media-sensitive-overlay,
    html.dark .js-media .js-media-sensitive-overlay,
              .js-card-container .js-media-sensitive-overlay,
    html.dark .js-card-container .js-media-sensitive-overlay  {
        height: 100%;
        width: 100%;
        overflow: hidden;
    }

              .js-media .js-media-sensitive-overlay:not(.is-large),
    html.dark .js-media .js-media-sensitive-overlay:not(.is-large),
              .js-card-container .js-media-sensitive-overlay:not(.is-large),
    html.dark .js-card-container .js-media-sensitive-overlay:not(.is-large)  {
        /*for small or medium media*/
        white-space: nowrap;
        font-size: 10px;
        min-height: auto;
    }

              .js-media .js-media-sensitive-overlay.is-large,
    html.dark .js-media .js-media-sensitive-overlay.is-large,
              .js-card-container .js-media-sensitive-overlay.is-large,
    html.dark .js-card-container .js-media-sensitive-overlay.is-large  {
        /*for large media*/
        display: flex;
        align-items: center;
        justify-content: center;
        white-space: pre-wrap;
    }

              .js-media .js-media-sensitive-overlay > div,
    html.dark .js-media .js-media-sensitive-overlay > div,
              .js-card-container .js-media-sensitive-overlay > div,
    html.dark .js-card-container .js-media-sensitive-overlay > div {
        position: absolute;
        left: auto;
        top: auto;
        width: auto;
        height: auto;
        margin: auto;
    }

              .js-media .js-media-sensitive-overlay:not(.is-large) > div,
    html.dark .js-media .js-media-sensitive-overlay:not(.is-large) > div,
              .js-card-container .js-media-sensitive-overlay:not(.is-large) > div,
    html.dark .js-card-container .js-media-sensitive-overlay:not(.is-large) > div {
        /*for small or medium media (large media don't need to be modified)*/
        padding: 0px 8px 2px!important;
    }


              .js-quote-detail.padding-al.margin-b--8.margin-tm,
    html.dark .js-quote-detail.padding-al.margin-b--8.margin-tm {
        padding: 4px 10px!important;
        margin-top: 4px!important;
        margin-bottom: 4px!important;
    }

              .js-tweet:not(.tweet-detail) > .txt-link,
    html.dark .js-tweet:not(.tweet-detail) > .txt-link {
        /*'Show this thread'*/
        position: relative;
        padding: 1px 5px;
        margin: -2px 0 0 calc(2px - var(--tweet-avatar-area-width));
        margin-bottom: 0px!important;
        top: -1px;
        border-radius: 4px;
        z-index: 2;
    }

              .tweet-footer,
    html.dark .tweet-footer {
        position: absolute;
        right: 0px;
        bottom: 0px;
        height: 0px;
        margin-top: 0px;
        margin-right: calc(10px - var(--tweet-horizontal-padding));

        border-radius: 4px;
    }
              .mdl .js-med-tweet .tweet-footer,
    html.dark .mdl .js-med-tweet .tweet-footer {
        position: static;
        height: auto;
        margin-top: 1px;
    }

              .tweet-footer .js-tweet-actions,
    html.dark .tweet-footer .js-tweet-actions {
        position: relative;
        top: calc(-1em - 6px);
        border-radius: 4px;
    }
              .mdl .js-med-tweet .tweet-footer .js-tweet-actions,
    html.dark .mdl .js-med-tweet .tweet-footer .js-tweet-actions {
        top: auto;
    }

              .is-protected-action,
    html.dark .is-protected-action {

        opacity: 1;
    }
              .is-protected-action > .tweet-action,
    html.dark .is-protected-action > .tweet-action {
        opacity: 0.5;
    }

    /*4th tweet-action-item is dropdown; need to avoid setting any z-index value on it, to avoid corruption*/
              .tweet-footer .tweet-action-item.margin-r--10,
    html.dark .tweet-footer .tweet-action-item.margin-r--10 {
        margin-right: 0px!important;
        padding-right: 10px;
    }
              .tweet-footer .tweet-action-item,
    html.dark .tweet-footer .tweet-action-item {
        background-color: var(--bgcolor-light-85);
    }
    html.dark .tweet-footer .tweet-action-item {
        background-color: var(--bgcolor-dark-85);
    }
    html.btd-on.dark .btd__og_dark_theme .tweet-footer .tweet-action-item {
        background-color: var(--bgcolor-og-dark-85);
    }
              .mdl .js-med-tweet .tweet-footer .tweet-action-item,
    html.dark .mdl .js-med-tweet .tweet-footer .tweet-action-item {
        background-color: transparent;
    }
    html.btd-on      .btd__actions_on_hover                    .tweet-footer .tweet-action-item,
    html.btd-on.dark .btd__actions_on_hover                    .tweet-footer .tweet-action-item,
    html.btd-on.dark .btd__actions_on_hover.btd__og_dark_theme .tweet-footer .tweet-action-item {
        background-color: transparent;
    }

              .tweet-footer .tweet-action-item:nth-of-type(-n + 3),
    html.dark .tweet-footer .tweet-action-item:nth-of-type(-n + 3) {
        position: relative;
        z-index: 3;
    }
              .tweet-footer .tweet-action-item:first-of-type,
    html.dark .tweet-footer .tweet-action-item:first-of-type {
        padding-left: 5px;
        border-radius: 4px 0 0 4px;
    }
              .tweet-footer .tweet-action-item:nth-of-type(3),
    html.dark .tweet-footer .tweet-action-item:nth-of-type(3) {
        padding-right: 5px;
        border-radius: 0 4px 4px 0;
    }

              .tweet-footer .tweet-action-item:nth-of-type(4),
    html.dark .tweet-footer .tweet-action-item:nth-of-type(4) {
        padding-left: 5px;
        border-radius: 4px;
    }

              .js-stream-item:hover .js-tweet-actions,
    html.dark .js-stream-item:hover .js-tweet-actions {
        z-index: 10;

        background-color: var(--bgcolor-light);
    }
    html.dark .js-stream-item:hover .js-tweet-actions {
        background-color: var(--bgcolor-dark);
    }
    html.btd-on.dark .btd__og_dark_theme .js-stream-item:hover .js-tweet-actions {
        background-color: var(--bgcolor-og-dark);
    }
              .mdl .js-med-tweet .js-stream-item:hover .js-tweet-actions,
    html.dark .mdl .js-med-tweet .js-stream-item:hover .js-tweet-actions {
        background-color: transparent;
    }

              .js-tweet-actions:hover,
    html.dark .js-tweet-actions:hover {
        z-index: 10;
    }

              .tweet-footer .js-tweet-actions:hover,
              .tweet-footer .js-tweet-actions:hover .tweet-action-item,
    html.dark .tweet-footer .js-tweet-actions:hover,
    html.dark .tweet-footer .js-tweet-actions:hover .tweet-action-item {
        background-color: var(--bgcolor-light-emphasized);
    }
    html.dark .tweet-footer .js-tweet-actions:hover,
    html.dark .tweet-footer .js-tweet-actions:hover .tweet-action-item {
        background-color: var(--bgcolor-dark-emphasized);
    }
    html.btd-on.dark .btd__og_dark_theme .js-tweet-actions:hover,
    html.btd-on.dark .btd__og_dark_theme .tweet-footer .js-tweet-actions:hover .tweet-action-item {
        background-color: var(--bgcolor-og-dark-emphasized);
    }
              .mdl .js-med-tweet .tweet-footer .js-tweet-actions:hover,
              .mdl .js-med-tweet .tweet-footer .js-tweet-actions:hover .tweet-action-item,
    html.dark .mdl .js-med-tweet .tweet-footer .js-tweet-actions:hover,
    html.dark .mdl .js-med-tweet .tweet-footer .js-tweet-actions:hover .tweet-action-item {
        background-color: transparent;
    }

    /*Message column*/
    /*note: .js-tweet specifically doesn't apply to messages*/
              .tweet-message,
    html.dark .tweet-message {
        position: relative;
        padding-left: var(--tweet-avatar-area-width);
    }

              .tweet-message .item-img.tweet-img,
    html.dark .tweet-message .item-img.tweet-img {
        margin-left: calc(0px - var(--tweet-avatar-margin-right) - var(--tweet-avatar-size));
        margin-right: var(--tweet-avatar-margin-right);
        width: var(--tweet-avatar-size);
    }

              .tweet-message .tweet-avatar.avatar,
    html.dark .tweet-message .tweet-avatar.avatar {
        width: var(--tweet-avatar-size);
        height: var(--tweet-avatar-size);

    }

              .tweet-message > .l-table > .l-cell,
    html.dark .tweet-message > .l-table > .l-cell {
        width: 100%;
    }

              .message-stream-item,
    html.dark .message-stream-item {
        padding: var(--tweet-vertical-padding) var(--tweet-horizontal-padding);
    }

              .message-stream-item .tweet,
    html.dark .message-stream-item .tweet {
        position: relative;
        padding-left: 0;
    }

              .message-stream-item .tweet > .tweet-header,
    html.dark .message-stream-item .tweet > .tweet-header {
        padding-left: var(--tweet-avatar-area-width);
    }

              .message-stream-item .tweet > .tweet-header .item-img.tweet-img,
    html.dark .message-stream-item .tweet > .tweet-header .item-img.tweet-img {
        float: initial;
        margin-left: calc(0px - var(--tweet-avatar-margin-right) - var(--tweet-avatar-size));
        margin-right: var(--tweet-avatar-margin-right);
        width: var(--tweet-avatar-size);
    }

              .message-stream-item .tweet > .tweet-header .tweet-avatar.avatar,
    html.dark .message-stream-item .tweet > .tweet-header .tweet-avatar.avatar {
        width: var(--tweet-avatar-size);
        height: var(--tweet-avatar-size);
    }

              .message-stream-item .tweet > .tweet-body,
    html.dark .message-stream-item .tweet > .tweet-body {
        padding-left: var(--tweet-avatar-area-width);
    }
              .message-stream-item .tweet > .tweet-body > .tweet-footer,
    html.dark .message-stream-item .tweet > .tweet-body > .tweet-footer {
        float: initial;
        position: absolute;
        top: auto;
        left: auto;
        right: 0;
        bottom: calc(1em + 6px);
    }

    :root {
        --btd--collapse-dms--icon-size: 24px;
    }

    html.btd-on .btd__collapse_dms .tweet-message {
        margin-left: calc(0px - var(--tweet-avatar-margin-right) - var(--btd--collapse-dms--icon-size));
        margin-right: var(--tweet-avatar-margin-right);;
    }

    html.btd-on .btd__collapse_dms .tweet-message .item-img.tweet-img {
        padding-left: calc(var(--btd--collapse-dms--icon-size) + var(--tweet-avatar-margin-left) + var(--tweet-avatar-margin-right));
    }




