/*[[smallerCards]]*/

/* Option: ExtremeMode */
/*[[extremeMode]]*/

/* Option: ExUsernameOrder */
/*[[exUsernameOrder]]*/

/* Option: ExExtreme */
/*[[extraExtreme]]*/
}

@-moz-document url-prefix("https://web.tweetdeck.com/") {
/* Nothing is here; this is only for categorisation on UserStyles.org. */
}

style_setting_options.value:
/*3 columns (33.3%)*/
          .column,
          .is-wide-columns .column,
          .is-narrow-columns .column,
html.dark .column,
html.dark .is-wide-columns .column,
html.dark .is-narrow-columns .column {
    width: calc(100% / 3);
}

/*Enabled*/
          .column-header,
          .column-header-temp,
html.dark .column-header,
html.dark .column-header-temp {
    max-height: 30px;
    height: 30px;
    line-height: 30px;
}

          .js-column-drag-handle,
html.dark .js-column-drag-handle {
    margin-top: 13px;
}

          .js-column-drag-handle,
html.dark .js-column-drag-handle {
    height: 14px;
}

          .column-header-title,
html.dark .column-header-title {
    height: 30px;
    line-height: normal;
}

          .column-title-back .icon,
          .column-title-back .Icon,
html.dark .column-title-back .icon,
html.dark .column-title-back .Icon {
    top: 15px;
}

          .js-action-header-button.margin-t--10.margin-r--10,
html.dark .js-action-header-button.margin-t--10.margin-r--10 {
    margin-top: 0px!important;
    margin-right: 0px!important;
}

/*Enabled*/
          .account-link.flex-auto,
html.dark .account-link.flex-auto {
    flex: initial;
}

          .tweet-timestamp.flex-shrink--0,
html.dark .tweet-timestamp.flex-shrink--0 {
    flex: 1 1 auto;
    min-width: 1px;
    min-height: 1px;
    text-align: right;
}

/* Smaller media */
          .js-media .media-size-small,
html.dark .js-media .media-size-small {
    height: 45px;
}

          .js-media .media-size-medium,
html.dark .js-media .media-size-medium {
    height: 90px;
    width: 70%;
}

          .js-media.media-grid-container.media-size-medium,
html.dark .js-media.media-grid-container.media-size-medium {
    height: auto;
    width: 100%;
}

          .js-media.media-grid-container.media-size-medium > .media-grid-2,
          .js-media.media-grid-container.media-size-medium > .media-grid-2-mirror,
html.dark .js-media.media-grid-container.media-size-medium > .media-grid-2,
html.dark .js-media.media-grid-container.media-size-medium > .media-grid-2-mirror {
    height: 45px;
}

          .js-media.media-grid-container.media-size-medium > .media-grid-3,
          .js-media.media-grid-container.media-size-medium > .media-grid-3-mirror,
          .js-media.media-grid-container.media-size-medium > .media-grid-4,
html.dark .js-media.media-grid-container.media-size-medium > .media-grid-3,
html.dark .js-media.media-grid-container.media-size-medium > .media-grid-3-mirror,
html.dark .js-media.media-grid-container.media-size-medium > .media-grid-4 {
    height: 94px;
}

/* Smallest media */
          .js-media .media-size-small,
          .js-media .media-size-medium,
html.dark .js-media .media-size-small,
html.dark .js-media .media-size-medium {
    width: 30%;
    height: 45px;
}

          .js-media .media-size-medium,
html.dark .js-media .media-size-medium {
    width: 50%;
}

          .js-media.media-grid-container.media-size-medium,
html.dark .js-media.media-grid-container.media-size-medium {
    height: 45px;
    width: 100%;
}

          .js-media.media-grid-container.media-size-medium .media-image-container,
html.dark .js-media.media-grid-container.media-size-medium .media-image-container {
    height: 100%;
