css:
@-moz-document url-prefix("https://genius.com/") {
body {
    background: url("/*[[home-page]]*/") top center;
    background-attachment: fixed;
    background-size: cover;
    color: #fff;
    text-shadow: 1px 1px #000;
}

/* header*/
.header-primary {
    background: transparent;
}
.quick_search--header {
    border: 1px solid #000;
    background-color: rgba(34, 34, 34, 0.5);
    box-shadow: 0 0 3px #000 inset;
}
.global_search-search_icon {
    filter: invert(100%);
}
.quick_search--header:focus::-moz-placeholder,
.quick_search--header {
    color: #fff;
    text-shadow: 1px 1px #000;
}
.full_width_button {
    color: #fff;
    background-color: #111;
}
.full_width_button:hover {
    background-color: #222;
}
.feed_dropdown--darker_background {
    background-color: #000;
}
.mini_card {
    background-color: rgba(17, 17, 17, 0.5);
    border: 1px solid #000;
}
.mini_card:not(.mini_card--transparent):hover {
    background-color: #222;
}
.text_label--gray {
    color: #fff;
}
.header-primary .logo_link::before {
    background-color: #ffff64;
}
.header-action.header-action--active,
.header-action:hover,
.header-action {
    color: #fff;
    text-shadow: 1px 1px #000;
}
.header-nav_menu {
    background-color: rgba(0, 0, 0, 0.7);
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
}
.nav_menu-link {
    text-shadow: 1px 1px #000;
}

/* horizontal session */
.horizontal_section--white_background {
    background-color: transparent;
}
.text_label {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
    color: #fff;
}
.home_featured_latest_story {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
    color: #fff;
}

/* song list */
.text_label--gray.text_label--button {
    border-color: transparent;
}
.tooltip {
    background: #000;
    color: #fff;
}
.tooltip-list_item--bottom_border {
    border-bottom: 1px solid #222;
}
.chart_row {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}
.chart_row:hover {
    background-color: rgba(17, 17, 17, 0.5);
    border: 1px solid #000;
}
.c.chart_row {
    transition: background-color .25s ease;
    border-bottom: 1px solid #222;
}
.home_show_more {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}

/* video series */
.horizontal_section--black_background {
    background-color: transparent;
}
.home_grid-section {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}

/* join community */
.column_layout-column_span--half:first-child:not(.column_layout-column_span--force_padding),
.column_layout-column_span--one_quarter:first-child:not(.column_layout-column_span--force_padding),
.column_layout-column_span--three_quarter:first-child:not(.column_layout-column_span--force_padding) {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}
.column_layout-column_span--big_left_margin {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}
.square_button,
.text_label--button {
    color: #fff;
}

/* video page  */
.video_page--black_background {
    background: transparent;
}
.video_carousel--half_black_background {
    background: rgba(0, 0, 0, 0.5);
}
.video_carousel-video-card--intro_card_text {
    background: rgba(0, 0, 0, 0.5);
}
.video_carousel-video-card-logo {
    filter: invert(100%);
}
.video_carousel-video-title {
    color: #fff;
}
.video_carousel-video--active .video_carousel-video-image,
.video_carousel-video--active .video_carousel-video-title {
    filter: brightness(.7);
}
.white_container,
.grid_layout {
    background-color: transparent;
}
.article_info_bar {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}
.inline_icon {
    fill: #fff;
}
.grid_layout--main {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
    padding: 15px;
}
.article_rich_text_formatting {
    color: #fff;
}
.article_rich_text_formatting .g_list-item-content-body > a,
.article_rich_text_formatting .g_list-item-header-title > a,
.article_rich_text_formatting em > a,
.article_rich_text_formatting h1 > a,
.article_rich_text_formatting h2 > a,
.article_rich_text_formatting h3 > a,
.article_rich_text_formatting h4 > a,
.article_rich_text_formatting h5 > a,
.article_rich_text_formatting h6 > a,
.article_rich_text_formatting li > a,
.article_rich_text_formatting p > a,
.article_rich_text_formatting small > a,
.article_rich_text_formatting strong > a {
    color: #fff;
}
.article_rich_text_formatting .g_list-item-content-body > a:not(:hover),
.article_rich_text_formatting .g_list-item-header-title > a:not(:hover),
.article_rich_text_formatting em > a:not(:hover),
.article_rich_text_formatting h1 > a:not(:hover),
.article_rich_text_formatting h2 > a:not(:hover),
.article_rich_text_formatting h3 > a:not(:hover),
.article_rich_text_formatting h4 > a:not(:hover),
.article_rich_text_formatting h5 > a:not(:hover),
.article_rich_text_formatting h6 > a:not(:hover),
.article_rich_text_formatting li > a:not(:hover),
.article_rich_text_formatting p > a:not(:hover),
.article_rich_text_formatting small > a:not(:hover),
.article_rich_text_formatting strong > a:not(:hover) {
    background: rgba(0, 0, 0, 0.5);
    text-shadow: none;
}
.article_info_and_action_box {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}
.square_button .inline_icon {
    fill: #fff;
}
.voting {
    background-color: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}
.square_input {
    background-color: rgba(0, 0, 0, 0.5);
    border: .15rem solid #000;
    color: #fff;
}
.song_body {
    background-color: rgba(0, 0, 0, 0.5);
    border: .1px solid #000;
    position: relative;
}
.referent {
    background-color: transparent;
    border: none;
    box-shadow: none;
}
.referent:hover {
    background-color: rgba(34, 34, 34, 0.5);
    border: 1px solid #000;
    box-shadow: .01em 0 0 #fff, -.01em 0 0 #fff;
}
.annotation_sidebar_arrow::before {
    background: #000;
}
.annotation_sidebar_unit {
    background: #000;
}
.song_story_featured_story_placement {
    background-color: rgba(0, 0, 0, 0.5);
    border: 2px solid #000;
}
.song_story_featured_story_placement-label {
    background: transparent;
    border: .15rem solid transparent;
}
.song_story_featured_story_placement-text-description,
.song_story_featured_story_placement-text {
    color: #fff;
}
.bordered_media_container {
    background-color: transparent;
    border: .15rem solid transparent;
}
.videos-title {
    background-color: rgba(0, 0, 0, 0.5);
    border: .1px solid #000;
}
.track_listing a:hover,
.track_listing a,
.track_listing-track_number,
.recirculated_content-title {
    color: #fff;
}

/* artist page  */
.profile_identity_and_description {
    background-color: rgba(0, 0, 0, 0.5);
    border: .1px solid #000;
    padding-bottom: 1rem;
}
.vertical_album_card {
    color: #fff;
    background-color: rgba(0, 0, 0, 0.5);
    border: .1px solid #000;
}
.vertical_album_card-release_date {
    color: #fff;
}
.vertical_album_card:hover {
    background-color: rgba(34, 34, 34, 0.5);
}
.u-top_margin {
    background-color: rgba(0, 0, 0, 0.5);
    border: .1px solid #000;
}
.breadcrumbs {
    color: #fff;
    background-color: rgba(0, 0, 0, 0.5);
    border: .1px solid #000;
}
}
