css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
body {
 background: #262626 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAENCAYAAADOoA9GAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABZSURBVHjaYmBgYPjPACP+MYFIEPEflfUflwQRSojgUiyGX4IkJTTRi5P4R39T/tFByT8qa6OJof8GPJgGPjHQI/FTki+pWFCQXlQhsZiBmBFMwLlgwAgQYAAYPe0bQ81rlgAAAABJRU5ErkJggg==") top left repeat-x !important;
 color: #eee !important;
 font-family: arial !important;
}

a:link {
 color: #cccccc !important;
}

a:visited {
 color: #cccccc !important;
}

a:hover {
 color: #ffffff !important;
}

a:active {
 color: #ff0000 !important;
}

hr {
 background: transparent !important;
 width: 100% !important;
 height: 5px !important;
 border: 0 !important;
}

img {
 border: 1px solid #000000 !important;
 background-color: #ffffff !important;
 padding: 1px !important;
}

.ad-title {
 display: none !important;
}

#ad {
 display: none !important;
}

#footer {
 display: none !important;
}

#navtop {
 background: #000000 !important;
 width: 99.5% !important;
 text-align: center !important;
 font-weight: bold !important;
 color: #999 !important;
 padding: .4em !important;
 top: 0;
 z-index: 1;
}

#navtopr {
 display: none !important;
}

#navtop a:hover {
 background: #555 !important;
}

.logo,.logo font b span{
 color: #fff !important;
}

form {
 margin: 1em !important;
}

.inputtext, textarea {
 width: 295px !important;
}

.rules{
 display: none !important;
}

td.postblock {
 background: #444 !important;
 border: 1px solid #666 !important;
 color: #aaa !important;
}

td.reply {
background-color:#333 !important;
width: 100% !important;
padding: .2em !important;
border: 1px outset #333 !important;
color: #eee !important;
}

td.replyhl {
background-color:#4B4B4B !important;
width: 100% !important;
padding: .2em !important;
border: 1px outset #333 !important;
color: #eee !important;
}

.postername, .commentpostername, .omittedposts, .abbr {
 color: #fff !important;
}

.postertrip {
 color: #999 !important;
}

.filetitle, .replytitle {
 color: #999 !important;
 font-size: large;
}

.unkfunc {
 color: #999 !important;
}

.doubledash {
visibility: hidden !important;
}

th {
 background: #222 !important;
 padding: .2em !important;
 font-size: small;
 width: 200px !important;
}

#hd {
display: none;
}

.boxbar {
background: #444 !important;
color: #eee !important;
}

.box-outer {
border: none !important;
background: #333 !important;
color: #eee !important;
}

.box-inner {
 background: #333 !important;
color: #eee !important;
}

#filter-button, #option-button {
display: none;
}

#ft{
display: none;
}

.pages {
 background: #444 !important;
 border: none !important;
}

.pages td, .pages td b {
color: #eee !important;
}

#recent-images li{
 background: #333 !important;
}
pre {
   color: inherit !important;
   background: #333333 !important;
   font-size: 12pt ! important;
   border-left-color: gray !important;
   border-right-color: gray !important;
   border-top-color: gray !important;
   border-bottom-color: gray !important;
}

}
