css:
@-moz-document domain("bing.com") {
body {
    background: #151515!important;
}



/*header*/
#b_header {

    background-color: #909090!important;
}

.touchQuery .carousel .carousel-controls,
.touchQuery .carousel .items,
.touchQuery .carousel .item,
.touchQuery .carousel .carousel-controls .nav_left,
.touchQuery .carousel .carousel-controls .nav_right {
    background: #5a5a5a!important;
}



#miniheader table {
    background-color: #585858!important;
}



/*images*/
#ftrB {

    background-color: #909090!important;
}

#rfPane {
    background: #5d5d5d!important;
}

#ftr_pane {

    background-color: #636363!important;
}

.dg_b {

    background-color: #202020!important;/*was #4e4e4e*/
}

.inline_tile .carousel {
    background-color: #757575!important;
}

.inline_tile .carousel .item .card {
    background-color: #585858!important;
    outline: 1px solid #000000!important;
}




/*webpages*/
#b_results > li {
    background-color: #bbb!important;
}

#b_context .b_ans,
#b_context #wpc_ag {
    background-color: #bbb!important;
}



/*video*/
.mc_vtvc {
    background-color: #909090!important;
    outline: 1px solid #000000!important;
}



/*news*/
.newscontainer {
    background: #1f1f1f!important;
}
.bnewsitem,
.newsitem {
    background-color: #dcdcdc!important;
}

#trd_module {
    background: #5a5a5a!important;
}

.trd_scroller {

    background: #5a5a5a!important;
}



/*footer*/
.b_footer {
    background-color: #000000!important;
}
}
