css:
@-moz-document domain(public.txdps-rp.com) {
body {
    color: #949494;
    background: #3c3c3c;
    font-family: helvetica neue, Roboto, Arial, droid sans, sans-serif;
    font-size: 13px;
    font-weight: 400;
    line-height: 1.471;
}
.unAvailableUnits {
	background: #6d6d6d;
}
.left_col {
    background: #4e4e4e;
}
body .container.body .right_col {
    background: #2b2828;
}
.x_panel {
    position: relative;
    width: 100%;
    margin-bottom: 10px;
    padding: 10px 17px;
    display: inline-block;
    background: #3c3c3c;
	border: 1px solid #D6BB90;
    -webkit-column-break-inside: avoid;
    -moz-column-break-inside: avoid;
    column-break-inside: avoid;
    opacity: 1;
    transition: all .2s ease;
}
.container {
    width: 100%;
    padding: 0;
    background: #4e4e4e;
}
.nav-sm .nav.child_menu li.active, .nav-sm .nav.side-menu li.active-sm {
    border-right: 5px solid #ffffff;
}
.nav_menu {
    float: left;
    background: #3c3c3c;
    border-color: #D6BB90;
    border-bottom: 1px solid #D6BB90;
    margin-bottom: 10px;
    width: 100%;
    position: relative;
}
.table-bordered,
td,
.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th{
	    border: 1px solid #D6BB90;
}
h1.page-header {
    text-align: center;
    font-size: 50px;
    font-family: -webkit-pictograph;
    color: #D6BB90!important;
    border-color: #D6BB90;
}
i.fa.fa-home,
i.fa.fa-clock-o,
i.fa.fa-book,
i.fa.fa-warning,
i.fa.fa-tachometer,
i.fa.fa-bars,
i.fa.fa-chevron-up,
i.fa.fa-chevron-down,
i.fa.fa-sign-out.pull-right{
    color: #D6BB90;
}
.site_title i {
	border: 1px solid #D6BB90;
	color: #deb492!important;
}
img.img-circle.profile_img {
    background: #D6BB90;
}
footer {
    background: #3c3c3c;
    padding: 15px 20px;
    display: block;
}
.x_title {
    color: #D6BB90;
    border-bottom: 2px solid #D6BB90;
}
.modal-content {
    position: relative;
    background-color: #4c4c4c;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid #999;
    border: 1px solid rgba(0, 0, 0, .2);
    border-radius: 6px;
    outline: 0;
    -webkit-box-shadow: 0 3px 9px rgba(0, 0, 0, .5);
    box-shadow: 0 3px 9px rgba(0, 0, 0, .5);
}
.right_col {
	color: white;
}
button.btn.dropdown-toggle.bs-placeholder.btn-default{
    background: #3d3d3d;
    color: white;
}
.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover{
	color: black;
}


button.btn.dropdown-toggle.btn-default,
span.filter-option.pull-left {
    background: #3d3d3d;
    color: white;
}
div#ncic_weapon_return,
div#ncic_plate_return,
div#ncic_name_return{
    padding: 5px;
    background-color: #3c3c3c!important;
	color: #F7F7F7;
}
ul.dropdown-menu,
.dropdown-menu>li>a{
	color: white;
    background: #3d3d3d;
    border-color: #d4ba8f;
}
input.form-control {
    color: white;
    background: #3d3d3d;
}
.navbar-nav .open .dropdown-menu{
	border-color: #d4ba8f;
}
.top_nav .nav>li>a:focus, .top_nav .nav>li>a:hover, .top_nav .nav .open>a, .top_nav .nav .open>a:focus, .top_nav .nav .open>a:hover {
    background: #3c3c3c;
}
span.text {
    color: white;
}
ul.dropdown-menu.inner {
    background: #3d3d3d;
    color: white;
}
.dropdown-menu.open {
    color: white;
    background: #4c4c4c;
}
.nav-md ul.nav.child_menu li:before {
    background: #2e9cff;
}
.nav-sm ul.nav.child_menu {
    background: #4e4e4e;
}
input.btn.btn-xs
{
    color: #ff5454;
}
.nav.side-menu>li.current-page, .nav.side-menu>li.active {
    border-right: 5px solid #d6bb90;
}
.modal-dialog.modal-lg {
    color: #c2c2c2;
}
.btn-link {
	color: #f7f7f7;
}
input {
    -webkit-appearance: textfield;
    background-color: #4c4c4c;
    -webkit-rtl-ordering: logical;
    cursor: text;
    padding: 1px;
    border-width: 2px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}
.nav_title {
    width: 230px;
    float: left;
    background: #3e3d3d;
    border-radius: 0;
    height: 57px;
}
.nav.side-menu>li.active>a {
    text-shadow: rgba(0, 0, 0, .25) 0 -1px 0;
    background: linear-gradient(#717171, #333333), #000000;
    box-shadow: rgba(0, 0, 0, .25) 0 1px 0, inset rgba(255, 255, 255, .16) 0 1px 0;
}
.sidebar-footer {
    bottom: 0;
    clear: both;
    display: block;
    padding: 5px 0 0;
    position: fixed;
    width: 230px;
    background: #3c3c3c;
    z-index: 999;
}
input#ncic_name,
input#ncic_plate,
input#ncic_weapon{
    border-color: #d6bb90;
    color: white;
    background-color: #2b2828;
}
a {
    color: #d6d7d8;
    text-decoration: none;
}
.table-striped>tbody>tr:nth-of-type(odd){
    color: white;
	background: #7c7c7c;
}
ul#ui-id-1,
ul#ui-id-2,
ul#ui-id-3{
    color: white;
    background: #2b2828;
}
.sidebar-footer a {
    padding: 7px 0 3px;
    text-align: center;
    width: 25%;
    font-size: 17px;
    display: block;
    float: left;
    background: #3c3c3c;
    cursor: pointer;
}
.img-circle.profile_img {
    width: 70%;
    background: #888;
    margin-left: 15%;
    z-index: 1000;
    position: inherit;
    margin-top: 20px;
    border: 1px solid rgba(52, 73, 94, .44);
    padding: 4px;
}
}
