css:
@-moz-document domain("mybroadband.co.za") {
a:not([class^="fauxBlockLink"]):not([class^="subNodeLink"]) {
    color: white;
}
.contentRow-main > a {
    color: black;
}
.p-footer,
.p-navSticky {
    max-width: 85% !important;
}
.p-pageWrapper,
body {
    color: white !important;
    max-width: 100% !important;
}
.site-footer {
    width: 85% !important;
    margin: auto;
}
#mainNav ul li a {
    color: white;
}
.p-pageWrapper,
body,
.p-nav,
.p-sectionLinks {
    color: white;
    background-color: #030513 !important;
}
.message-inner,
.message-cell.message-cell--user,
#mainNav {
    color: white;
    background-color: #090B19 !important;
}
.message-cell.message-cell--user,
.message-cell.message-cell--action {
    position: relative;
    border-right: 2px solid #4F4E4C !important;
}
.p-body-inner {
    max-width: 90% !important;
}
.bbCodeBlock-title {
    color: #fff !important;
    background: #636363 !important;
}
.bbCodeBlock {
    color: #fff !important;
    background: #030513;
    border: 1px solid #4F4E4C !important;
    border-left-color: #4F4E4C !important;
    border-left: 3px solid #4F4E4C !important;
}
.fr-wrapper::-moz-selection {
    color: white;
    background: #e99d91 !important;
    background-color: rgb(233, 157, 145)!important;
}
.block-filterBar {
    border-bottom: 1px solid #000 !important;
}
.likesBar {
    background-color: #030513;
}
[class^="block"],
.structItemContainer,
.structItem {
    background: #030513 !important;
}
.p-footer,
.p-navSticky,
.p-sectionLinks {
    width: 100% !important;
}
.tabs--standalone {
    background: #cecece1a !important;
}
.pageNav-jump,
.pageNav-page--earlier,
.pageNav-page--current,
.pageNav-page,
.formSubmitRow-bar {
    color: white;
    background: #050715 !important;
}
[class^="pageNav-page"]:hover {
    filter: opacity(80%);
}
#top_menu {
    filter: invert(100%);
    background: #fcfaec;
}
[class^="p-navEl"] {
    color: white !important;
    background-color: inherit !important;
}
[class^="structItem"] {
    color: white;
}
[class^="structItem-pageJump"] > a {
    background-color: transparent;
}
[class^="structItem-pageJump"] > a:hover {
    border-color: yellow;
    background-color: transparent;
    color: yellow;
}
.actionBar-action {
    color: white !important;
}
.fr-element {
    background-color: #050715;
    color: white;
}
.fr-element > p {
    color: white;
}
.fr-wrapper > p {
    color: white;
    background-color: grey;
}
.fr-toolbar,
.input {
    filter: invert(100%);
}
.button--link,
.menu-linkRow,
.pika-single {
    filter: invert(90%);
}
body {
    overflow-y: unset !important;
}
.pairs > dd {
    color: white;
}
.button-text  {
    color: black;
}
}
