@-moz-document regexp("chrome-extension:\\/\\/.*"), regexp("-moz-chrome-extension:\\/\\/.*") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
}

#header summary h2,

input[type="checkbox"]:not(.slider),
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

body {
	letter-spacing: 1px!important;
}

html {
    scroll-behavior: smooth;
}

body {
    scroll-snap-type: y mandatory;
}

#lint td[role="message"] {
    white-space: pre-line!important;
}

/*___ Fonts ________________________________________________________________________*/

#header h1,

.blocked-info p,
.newUI .style-name,

button
{
	font-size: 21px!important;
}

#header summary h2,

.CodeMirror pre.CodeMirror-line,
.CodeMirror pre.CodeMirror-line-like,
.active #filters-stats {
	font-size: 19px!important;
}

.filter-selection select {
	font-size: 17px!important;
}

#name,

.applies-to li .applies-value,

body,
label,
select
{
	font-size: 15px!important;
}

.style-name {
    line-height: 55px!important;
}

.blocked-info p
{
    line-height: 25px!important;
}

.beautify-hint {
	line-height: 18px!important;
}

/*___ Remove Items _________________________________________________________________*/

.checkmate > .svg-icon,
.resize-grip {
	display: none!important;
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

.filter-selection select {
    height: 28px!important;
    padding-right: 29px!important;
}

#installed .style-name .checker, #installed .style-name .svg-icon.checked {
    top: 17px;
}

.style-name {
    padding-left: 2rem;
}

/* Heights 
_____________________________________________________________________________________________________*/

#installed,

body,
html
{
	max-height: none!important;
}

.entry-content {
    height: 3rem;
}

#main-actions > * {
    padding-left: 2rem;
}

/* Widths
____________________________________________________________________________________________________*/

#stylus-popup,

body,
html
{
	max-width: none!important;
}

.sectioned .section {
	height: auto!important;
}

#stylus-popup {
    width: 50rem!important;
}

}

