    margin: 24px !important;
  }

  .aligned .svg-inline-wrapper
    {
    margin: -2px 0 0 0.6rem;
  }
    
  .applies-to ul
    {
    margin: 0 0 0 77px;
  }

  .beautify-options div:not(.select-resizer)
    {
    margin: 3px 6px !important;
  }
    
  .option.aligned
