@-moz-document regexp("chrome-extension:\\/\\/.*"), regexp("-moz-chrome-extension:\\/\\/.*") {
/* Font Settings */

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#toc li.current:not(:only-child),

.CodeMirror-activeline-gutter .CodeMirror-linenumber,

a[href]:hover,
h1,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label {
	color: #ff0!important;
}

/*__________________________________________________________________________________*/

.entry.usercss .style-name-link::after,
.oldUI .disabled h2::after,
.style-info[data-type=version],

body,
li,
p {
	color: #fff!important;
}

/*___ Khaki ________________________________________________________________________*/

#help-popup .title,

h1,
h2 {
	color: #F0E890!important;
}

/*___ Burlywood ____________________________________________________________________*/

h3,
h4 {
	color: #e0b880!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

.newUI .entry .style-info[data-type=age],

time {
	color: #07beff!important;
}

/*__________________________________________________________________________________*/

[placeholder="Enter a name"],
input:not([type]),
input[type="search"],
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #641a1a!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 10px!important;
}

/*__________________________________________________________________________________*/

.CodeMirror-line > span > span::selection,
.CodeMirror-line > span::selection,
.CodeMirror-line::selection,
.cm-matchhighlight,

::selection {
	background: #a454553b!important;
	color: #f9f9f947!important;
}

::placeholder {
	color: #f48f8f!important;
	opacity: 1!important;
}

/*___ Misc _________________________________________________________________________*/

.CodeMirror {
	color: #eee;
}

.cm-property {
	color: #f48f8f;
}

.cm-s-default .cm-atom {
	color: #ff8c00;
}

.cm-s-default .cm-builtin {
	color: #b18fff;
}

.cm-s-default .cm-comment {
	color: #c5c2be;
}

.cm-s-default .cm-def {
	color: #56ffef;
}

.cm-s-default .cm-keyword {
	color: #e52dff;
}

.cm-s-default .cm-meta {
	color: #ffcece;
}

.cm-s-default .cm-number {
	color: #e4ff00;
}

.cm-s-default .cm-qualifier {
	color: #01d0ff;
}

.cm-s-default .cm-string {
	color: #ff1a1a;
}

.cm-s-default .cm-tag {
	color: #24ff00;
}

.cm-s-default .cm-variable-3,
.cm-s-default .cm-type {
	color: #00ff9f;
}

.cm-variable {
	color: #8ff4ca;
}

.CodeMirror-cursor {
	border-left: 1px solid white;
}

.svg-icon.checked {
    fill: #00000000;
}

}

@-moz-document regexp("chrome-extension:\\/\\/.*"), regexp("-moz-chrome-extension:\\/\\/.*") {
/* Background Settings */

body,
html {
	background: #000!important;
}

/*___ Background Gray ______________________________________________________________*/

#options,

.CodeMirror-gutters,
.applies-to {
	background: #060505!important;
	background-color: #060505!important;
}

/*___ Background-color 0000 ________________________________________________________*/

#message-box-buttons,

.CodeMirror pre.CodeMirror-line-like,
.CodeMirror,
.CodeMirror-hints,
.search-result-meta
{
	background-color: #000!important;
}

/*__ Orange ________________________________________________________________________*/

button:disabled {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
}

button:disabled:hover {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

button:disabled:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
}

/*__ Blue ________________________________________________________________________*/

button
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: 0 .85rem!important;
	position: relative!important;
	z-index: 1!important;
}

button::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

button:hover::before
 {
  opacity: 1!important;
}

/*___ Plain ________________________________________________________________________*/

#confirm > div,
#help-popup,
#message-box > div,
#options-wrapper .options-column:nth-child(2),
.menu-items-wrapper,
.search-result {
	background-color: #1c1c1c!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
}

#confirm > div:hover,
#help-popup:hover,
#options-wrapper .options-column:nth-child(2).h,
.menu-items-wrapper:hover,
.search-result:hover {
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* _________________________________________________________________________________*/

/*___ Red Gradient _________________________________________________________________*/

.warnings {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/*__ Blue Gradient _________________________________________________________________*/

.active #filters-stats {
	background: linear-gradient(to bottom, #008b8b, #000 100%)!important;
}

/*__ Green Gradient ________________________________________________________________*/

data-focused-via-click {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important
}

/* __________________________________________________________________________________*/

/*__ Code ________________________________________________________________________*/

.CodeMirror-active,
.CodeMirror-code {
	background: rgb(71,3,5);
	background: radial-gradient(circle, rgba(71,3,5,1) 0%, rgba(0,0,0,1) 100%);
	background-repeat: round!important;
	background-attachment: fixed!important;
}

/*___ Highlight Background _______________________________________________________*/

#toc li.current:not(:only-child),
.CodeMirror-activeline-background,
.keymap-list tr:hover {
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Radial Background _________________________________________________________*/

.CodeMirror-gutter-background.CodeMirror-activeline-gutter,
.entry.usercss .style-name-link::after,
.oldUI .disabled h2::after
{
	background: radial-gradient(ellipse at center, #07ed07 0%, #0000 50%)!important;
	padding: 3px 16px!important;
}

}

@-moz-document regexp("chrome-extension:\\/\\/.*"), regexp("-moz-chrome-extension:\\/\\/.*") {
/* Image Settings */

.keymap-list tr,
.newUI .entry,
.search-result {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.keymap-list tr:hover,
