input[type="checkbox"]:checked:not(.slider)
{
	background: url(https://i.ibb.co/njmsz2z/green-light-on.png) no-repeat!important;
	box-sizing: border-box;
}

/* Red _______________________________________________________*/

.onoffswitch input + span::before,

input[type="checkbox"]:not(.slider)
{
	background: url(https://i.ibb.co/z5C1bGj/red-light-on.png) no-repeat!important;
	box-sizing: border-box;
}

.onoffswitch input + span::before,
.onoffswitch input:checked + span::before,

input[type="checkbox"]:checked:not(.slider),
input[type="checkbox"]:not(.slider)
{
	background-size: 24px!important;
	height: 25px!important;
	padding-left: 25px!important;
	width: 1px!important;
}

/* Help ___________________________________________________________________________________*/

[id*='help'] svg {
	background: url(https://i.ibb.co/H7KjkKR/help-147419-640.png) no-repeat!important;
	box-sizing: border-box;
}

[id*='help'] svg {
	background-size: 23px!important;
	height: 24px!important;
	padding-left: 24px!important;
	width: 1px!important;
}

/* Info ______________________________________________________________________________________________*/

.svg-icon.info {
	background: url(https://i.ibb.co/Lhfjqf4/information-sign-1024x1024.png) no-repeat!important;
	box-sizing: border-box;
}

.svg-icon.info {
	background-size: 19px!important;
	height: 20px!important;
	padding-left: 20px!important;
}

/* Settings ___________________________________________________________________________________*/

#details-wrapper [id*='settings'] svg,

[title="Options"] svg
{
	background: url(https://i.ibb.co/2dVYhWK/Setting-icon.png) no-repeat!important;
	box-sizing: border-box;
}

#details-wrapper [id*='settings'] svg,

[title="Options"] svg 
{
	background-size: 29px!important;
	height: 30px!important;
	padding-left: 30px!important;
	width: 1px!important;
}

}

@-moz-document regexp("chrome-extension:\\/\\/.*"), regexp("-moz-chrome-extension:\\/\\/.*") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
