css:
@-moz-document domain("harvestapp.com") {
body,
#main_content,
#sub_nav,
.nav,
.clearfix,
nav #sub_nav,
.js-nav,
.main-navigation,
.footer,
#global-header,
#global-footer {
	background: #222 !important;
	color: #eee !important;
}

.main-navigation,
.nav-blue,
div.wrapper.clearfix,
footer#global-footer {
    background: #111 !important;
}

.page-top-line h1 {
	color: white !important;
}
.btn-action {
	color: #111 !important;
}
.overview {
	background-image: linear-gradient(#444, #333) !important;
}
.overview h2 {
	color: #111 !important;
	padding-top: 5px !important;
}
.overview a:hover {
	color: #FFF !important;
	background-color: #111 !important;
	text-decoration: underline !important;
}
.running,
.day-view-entry td,
.hui-empty {
	color: white !important;
	background-image: linear-gradient(#444, #333) !important;
}
.big_link span {
	color: white !important;
}
.harvest_newsfeed a {
	color: white !important;
}
.hui-empty {
	border: 1px solid #333 !important;
}
.js-day,
.test-not-today,
.day-view-week-nav,
.hui-button,
.day-view-week-nav #day-view-week-nav-total {
	background-image: linear-gradient(#444, #272727) !important;
	color: white !important;
}
.hui-button a {
	color: white !important;
}
.day-view-duplication button {
	color: #888 !important;
}
.is-today {
	background-image: linear-gradient(#323232, #191919) !important;
	color: white !important;
}
.hui-button-selected {
	background-image: linear-gradient(#2A2A2A, #111) !important;
	color: white !important;
}
.flash-message {
	background: #F76900 !important;
	color: white !important;
	border: 1px solid white !important;
}

.button-new-time-entry {
	background: rgba(255, 255, 255, 0) !important;
}

.calendar-drawer,
.calendar-header {
    background-image: linear-gradient(#444, #333) !important;
}

.hui-dialog-medium,
.hui-dialog,
.hui-dialog-title {
    background-image: linear-gradient(#444, #333) !important;
    color: white !important;
}

.hui-well,
.dashboard-well {
    background-image: linear-gradient(#444, #333) !important;
    color: white !important;
}

.hui-label,
.hui-row a {
	color: white !important;
}

.text-default {
	color: #888 !important;
}

.sub-nav-tabs a:hover,
.sub-nav-tabs a:active,
.sub-nav-tabs a.current {
    box-shadow: inset 0 -2px 0 #111
}
}
