css:
@-moz-document url("https://catwar.su/cw3/")
{
  div#history_block div
  {
    background-color: #778899;
  }

  div#thdey
  {
    margin-left: 10px;
  }

  #history div
  {
    background-image: url('http://d.zaix.ru/6nBF.png');
    margin-top: 10px;
    margin-left: 5px;
    color: transparent;
  }

  span#location
  {
    color: #778899 !important;
    position: fixed;
    width: 170px;
    top: 243px;
    left: 25px;
    background-color: #627182;
    border: 0.5px solid #627182;
    border-radius: 10px;
    text-align: center;
    padding: 5px;
    font-weight: bold;
  }

  a#history_clean
  {
    margin-left: 5px;
  }

  span#ist
  {
    margin-left: 5px;
    color: #778899 !important;
  }

  div#cages_div
  {
    opacity: 1 !important;
  }

  select
  {
    background-color: #151719;
    color: #778899;
  }

  body
  {
    background-color: #161a1e;
    color: #778899;
  }

  .small
  {
    font-size: 14px;
    margin-left: 42%;
  }

  div#tir
  {
    margin-left: 10px;
    margin-top: 10px;
  }

  #akten
  {
    margin-left: 5px;
  }

  a
  {
    color: #b0b7c6;
  }

  input
  {
    background-color: #161a1e;
    color: #778899;
    border: 0.5px solid #23282b;
    border-radius: 5px;
  }

  hr
  {
    border: 0.5px solid #23282b;
  }

  .ui-slider
  {
    background: #161a1e;
    border: 0.5px solid #23282b;
    margin-right: 50px;
  }

  .ui-slider .ui-slider-handle
  {
    background: #383838;
    border: 0.5px solid #23282b;
  }

  .move_name
  {
    color: #000000;
  }

  .nick
  {
    color: #23282b;
  }

  .adopt-id
  {
    color: #fff;
  }

  .hotkey
  {
    background-color: #161a1e;
    color: #778899;
    border: 0.5px solid #23282b;
    border-radius: 5px;
  }

  .ui-scroll
  {
    background-color: #b0b7c6;
  }

  #fightPanel
  {
    background-color: #2d2d2d;
    border: 0.5px solid #2d2d2d;
  }

  #block_mess
  {
    margin-left: 10px;
  }

  #tr_chat
  {
    background-color: #2d2d2d;
    padding: 0px;
  }

  #chat_msg
  {
    background-color: #2d2d2d;
    border: 0px;
  }

  #tr_actions
  {
    background-color: #1c1c1c;
    border: 0px;
  }

  #block_deys
  {
    margin-top: 5px;
  }

  #mit
  {
    background-color: #161a1e;
    color: #778899;
    border: 0.5px solid #23282b;
    border-radius: 5px;
  }

  #mitok
  {
    margin-top: 2px;
    background-color: #161a1e;
    color: #778899;
    border: 0.5px solid #23282b;
    border-radius: 5px;
  }

  #main_table
  {
    border: 0px;
    margin-top: 10px;
  }

  #tr_tos
  {
    display: none;
  }

  #tr_mouth
  {
    background-color: #1c1c1c;
  }

  #info_main
  {
    background-color: #1c1c1c;
  }

  #family
  {
    background-color: #1c1c1c;
  }

  a#history.toggle
  {
    color: #778899;
    font-size: 16px;
    margin-left: 10px;
    margin-bottom: 5px;
  }

  #history
  {
    background-color: #1c1c1c;
    width: 100%;
    margin-left: 10px;
  }

  #parameter
  {
    position: fixed;
    width: 185px;
    top: 20px;
    left: 20px;
    border-radius: 10px;
    text-align: left;
    padding: 3px;
  }

  a#parameters.toggle
  {
    display: none;
  }

  #block_mess
  {
    margin-top: 5px;
    margin-bottom: 5px;
  }

  #family
  {
    display: none;
  }

  #cages
  {
    background-color: transparent;
  }

  #text
  {
    color: #fff;
  }

  #might_table
  {
    margin-bottom: 21px;
  }

  p#error
  {
    top: 270px;
    left: 25px;
    background-color: #23282b;
    border: 0.5px solid #23282b;
  }

  #items
  {
    padding-left: 17px;
    padding-top: 5px;
    padding-bottom: 5px;
  }

  input:focus
  {
    background-color: #383838;
  }

  #newchat
  {
    background-color: #6067c6;
    color: #161a1e;
  }

  #newls
  {
    background-color: #6067c6;
    color: #161a1e;
  }

  a:hover
  {
    color: #6067c6;
  }

  .myname
  {
    background-color: #6067c6;
    color: black;
  }

  .title
  {
    display: none;
  }

  #tr_sky
  {
    display: none;
  }
}
