css:
@-moz-document domain(facebook.com)
{
.UIFilterList_ItemLink,.UIFilterList_Selected
{
   max-width:22px !important;
   text-indent: 30px !important;
   overflow:hidden !important;
   border:1px solid #D6D9DE !important;
}
.UIMutableFilterList_Toggle,.UIMutableFilterList_Footer
{
   max-width:22px !important;
   overflow:hidden !important;
   border:none !important;
}
.UIMutableFilterList_Toggle,.UIMutableFilterList_Footer a
{
   color:transparent !important;
}
.UIMutableFilterList_Toggle span
{
   margin-left:5px !important;
}
.UIMutableFilterList_Plus
{
   margin-left:11px !important;
}
.UIFilterList_ItemLink
{
   padding: 2px 5px !important;
}
.UIFilterList_Icon
{
   margin:0 5px !important;
}
.UIFilterList_Selected, .UIFilterList_Selected * { max-width:32px !important;border:none !important; }
.UIFilterList_LastItem, .UIFilterList_FirstItem { border: none !important; }
#home_stream
{
   width:620px !important;
   margin-left:-90px !important;

}
}
