css:
/*
Style By Alireza Ghezeli
Greenweb.ir
*/
@font-face {
  font-family: 'Roboto';
  src: url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Light/Roboto-Light-webfont.eot');
  src: local('Roboto Light'), local('Roboto'), url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Light/Roboto-Light-webfont.ttf') format('truetype');
  font-weight: 300;
  font-style: normal;
}
@font-face {
  font-family: 'Roboto';
  src: url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Regular/Roboto-Regular-webfont.eot');
  src: local('Roboto Regular'), local('Roboto'), url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Regular/Roboto-Regular-webfont.ttf') format('truetype');
  font-weight: 400;
  font-style: normal;
}
@font-face {
  font-family: 'Samim';
  src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.eot');
  src: local('Samim'), local('Samim'), url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.ttf') format('truetype');
  font-style: normal;
}

/* ========================================= */
body {
    font-family: Samim, Roboto,tahoma, "Helvetica Neue", Arial, Helvetica, sans-serif !important;
    font-size: 12px;
}
body.body-board-view {
    background-color: rgba(0, 150, 210, 1.0)!important;
}
textarea {
    font-family: Samim, Roboto!important;
    direction: rtl;
}


.board-menu {
    margin-top: 0!important;
    position: fixed!important;
	z-index: 60!important;    
}
.list {
    border-radius: 10px!important;
}
.list-card-title {
    font-size: 13px;
    line-height: 1.7em;
}
.list-card-operation {
    right: auto;
    left: 3px;
    padding: 2px;
}
.checklist-item-details-text {
    direction: rtl;
}
.card-label.mod-card-front {
    float: right;
}
.board-wrapper.is-show-menu .board-header-btns.mod-right {
    visibility: visible;
    right: -303px;
}
.header-logo-default:after {
    content: "Greenweb";
    color: white;
    top: 21px;
    position: absolute;
}

.checklist-item-checkbox {
    right: 5px !important;
    left: inherit;

}
.checklist-item {
    padding-right: 26px!important;

}
.card-label.mod-card-front {
    text-align: center;
    line-height: initial;
    font-size: 8px;
    height: 12px;
}
.comment-container p {
    direction: rtl;
}







/* i really want this to be global */


.card-short-id {
    display: inline !important;
    border: 1px solid #d9d9d9;
    border-bottom-color: #d3d3d3;
    border-radius: 2px;
    color: #737373;
    cursor: default;
    float: left;
    font-size: 11px;
    line-height: 16px;
    height: 16px;
    margin-right: 4px;
    padding: 0 4px;
    position: relative;
    text-decoration: none;  
}
  
body .list-header-num-cards.hide {
	display: inline-block;
	font-size: 11px;
	color: #fff;
	margin-left: 10px;
}



.list-header-extras {
    position: absolute;
    left: 4px;
    top: 4px;
    z-index: 1;
}

.list-header-extras-menu, .list-header-extras-subscribe {
    color: #fff;
}
    
.list  {
	border: 1px solid #00000030;
    box-shadow: 2px 2px #00000012;

}

.list-cards{
        background: #0000000f;
}

.list-card-title {
    text-align: right;
}

.list-header textarea {
    text-align: right;
}

.list-header-extras-menu:hover {
    background: #cdd2d466;
}

div.list.js-list-content {
	height: 100%;
}

div#board div.js-list.list-wrapper {
	margin: 1px;
}
div#board div.js-list.list-wrapper:nth-child(1) div.list.js-list-content div.list-header {
	background-color: #B0C4DE;
	color: #ecf0f1;
	padding: 14px;
    border-radius: 10px 10px 0 0;
}
div#board div.js-list.list-wrapper:nth-child(2) div.list.js-list-content div.list-header {
	background-color: 	#50BFE6
