@-moz-document domain("bangkok.learn.nae.school") {
/*Base*/
body {
    color: #999;
    background:url("/*[[image]]*/")no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
.block {
    background-color: #151515;
    opacity:/*[[op]]*/;
}
hr {
    border-top: none;
    border-bottom: 1px solid #4c4c4c
}
.calendar_event_site {
    background-color: #333;
}
.breadcrumb {
    background-color: #151515;
    opacity:/*[[op]]*/;
}
.navbar-inner {
    height: 30px;
}
.course-content ul li.section.main {
    border-bottom: 2px solid #222 !important;
    border-top: none !important;
    border-left: none !important;
    border-right: none !important;
}
ul.dropdown-menu {
    background-color: #dedede
}
.signuppanel {
    display: none;
}
div.box {
    border: none !important;
}
div.box.modal-header {
    background-color: #333;
}
div.box.modal-body {
    background-color: #222;
}
div.box.modal-footer {
    background-color: #333;
    border-radius: 0px;
    box-shadow: none;
}
li.active a {
    background-color: #4c4c4c !important;
    border: 1px solid #4c4c4c !important;
    color: #000 !important;
}
.nav-tabs {
    border-bottom: 1px solid #4c4c4c
}
.info{
    display:block !important;
}
.coursebox.even{
    background-color:#4c4c4c !important;
}

/*Messaging*/
div.searchtextarea {
    background-color: #1e1e1e !important;
}
div.messages-header {
    border: none !important;
    background-color: #1e1e1e
}
div.messaging-area {
    border: none !important;
}
div.contact {
    border: none !important;
    border-bottom: 2px solid #444 !important;
}
div.picture {
    border: none !important;
    border-bottom: 2px solid #444 !important;
}
div.message-text-container {
    background-color: #1e1e1e;
    border: none !important;
}
div.contacts-area {
    border: none !important;
}
div.tabs {
    border: none !important;
}
div.tabimage {
    display: none;
}
div.selected {
    background-color: #333 !important;
}
div.tabcontacts {
    background-color: #1e1e1e !important;
    line-height: 350%;
}
div.tabconversations {
    background-color: #1e1e1e !important;
    line-height: 350%;
}
textarea {
    background-color: #1e1e1e !important;
    border-radius: 0px !important;
    border: none !important;
    color: #ccc;
}
div.response {
    background-color: #1e1e1e!important;
    border-radius: 0px !important;
    border: none !important;
}
div.content.right {
    background-color: #222!important;
    border: none !important;
}
div.content.left {
    border: none !important;
    background-color: #1e1e1e;
}

/*Text*/
h2 {
    color: #666 !important;
    text-shadow: none !important;
}
em {
    color: #555;
}
::placeholder {
    color: #555 !important;
}
a {
    color: #999;
}
a:hover {
    color: white !important;
}
a:visited {
    color: #999
}
.m-t-1 {
    color: #555
}
.info {
    display: none;
}
.breadcrumb li {
    text-shadow: none;
}
.arrow {
    color: #555;
}
span {
    color: #777 !important;
}
span.menu-action-text {
    font-size: 14px !important;
}
.label.label-info {
    background-color: #333;
}

/*Images*/
div.attwrapper a img {
    height: 0px;
}
div.block_action {
    display: none;
}
a.logo {
    float: left;
    display: block;
    overflow: hidden;
    -moz-box-sizing: border-box;
    -box-sizing: border-box;
    height: 75px;
    width: 90%;
    padding-left: 90%;
    background: url("https://i.imgur.com/aOEyPfP.png") no-repeat, url("https://i.imgur.com/UrX966d.png") no-repeat right;
    margin-top: 50px;
    margin-bottom: 15px;
}
a img.img-responsive[alt="GlobalCampus"] {
    display: block;
    overflow: hidden;
    -moz-box-sizing: border-box;
    -box-sizing: border-box;
    height: 47px;
    width: 115px;
    padding-left: 115px;
    background: url("https://i.imgur.com/JXhej4T.png")no-repeat;
}
img[alt="Line"] {
    filter: brightness(30%);
}
.hwstats_tick {
    background: url("https://i.imgur.com/z5TzETn.png")no-repeat !important;
}
.hwstats_flag {
    background: url("https://i.imgur.com/jx3C1zF.png")no-repeat !important;
}
.hwstats_credit {
    background: url("https://i.imgur.com/ttmmC2F.png")no-repeat !important;
}
.empty-placeholder-image-lg {
    display: none;
}
.icon {
    display: none;
}

/*Forms*/
input {
    background-color: #1d1d1d !important;
    border: solid #1e1e1e !important;
    box-shadow: none !important;
    border-radius: 0px !important;
    color: #555 !important;
    background-image: none !important;
    text-shadow: none !important;
}
.searchButton {
    background-color: #333;
    border: none;
    box-shadow: none;
    border-radius: 0px;
    color: #999;
}
.btn {
    display: none;
}

/*Icons*/
a.frontcata span {
    font-size: 0px !important;
}
a.frontcata {
    display: block;
    overflow: hidden;
    -moz-box-sizing: border-box;
    -box-sizing: border-box;
    height: 91.5px;
    width: 91.5px;
    padding-left: 91.5px;
    filter: brightness(85%);
}
