css:
@-moz-document domain("seznam.cz") {
:root {
    --bg-color-background: #23232d;
    --bg-color-lighter: #2c2c36;
    --bg-color-lightest: #3b3b4b;
    --text-color-primary: #dfdfdf;
}

body {
    background-color: var(--bg-color-lighter, #2c2c36);
    color: var(--text-color-primary, #dfdfdf);
}

.ui--image-skin .content-underlay {
    background: hsla(195, 0%, 10%, .95);
}

.content-underlay {
    background-color: var(--bg-color-background, #23232d);
}

.gadget--email .email__content--link {
    color: var(--text-color-primary, #dfdfdf);
}

.tab-button--active:focus,
.tab-button--active:hover {
    color: var(--text-color-primary, #dfdfdf);
}

.tab-button:focus,
.tab-button:hover {
    color: var(--text-color-hover, #3b3b4b);
    ;
}

.tab-button:focus,
.tab-button:hover {
    color: var(--text-color-hover, #3b3b4b);
    ;
}

.tab-button {
    color: var(--text-color-primary, #dfdfdf);
}

.link--gadget-title {
    color: #9b9bb0;
}

.style-tags--gadget-title a {
    color: #9b9bb0;
}

.link {
    color: #9b9bb0 !important;
}

.expander__button,
.expander__button:focus,
.expander__button:hover {
    color: var(--text-color-primary, #dfdfdf);
}

.gadget--sluzby .weather__forecast-temp {
    color: var(--text-color-primary, #dfdfdf);
}

.action__text {
    color: var(--text-color-primary, #dfdfdf);
}

.atm-tag {
    color: var(--text-color-primary, #dfdfdf);
}

.color-channel {
    color: var(--text-color-primary, #dfdfdf);
}

.text-crop--sham::before {
    background: none;
}

.gadget__promo {
    background: var(--bg-color-lighter, #2c2c36);
}

.hp-search .logo {
    background-color: var(--text-color-hover, #3b3b4b);
    border-radius: 0px 15px 15px 0px;
    padding: 10px;
    left: -1.0rem;
}

.hp-search .krasty {
    background-color: var(--text-color-hover, #3b3b4b);
    border-radius: 0px 15px 0px 0px;
    padding: 15px;
    top: 0rem;
    right: -1.0rem;
}

.fancy-checkbox__text {
    color: var(--text-color-primary, #dfdfdf);
}

.button--scroll-up,
.button--submit {
    background-color: #f13f3f;
    color: var(--text-color-primary, #dfdfdf);
    font-weight: bolder;
}

.button--scroll-up.button--pressed-down,
.button--scroll-up:focus,
.button--scroll-up:hover,
.button--submit.button--pressed-down,
.button--submit:focus,
.button--submit:hover {
    background-color: #f13f3f;
    color: var(--text-color-primary, #dfdfdf);
    font-weight: bolder;
}

.ogm-sticky-header__right-side {
    background: transparent;
}

.color-action {
    color: var(--text-color-primary, #dfdfdf);
}

.skin-default seznam-pocitadlolibise, seznam-pocitadlolibise {
    --primary-color: var(--text-color-primary, #dfdfdf);
}

/** Email */
nav {
    background-color: var(--bg-color-background, #23232d);
}

nav li a {
    color: var(--text-color-primary, #dfdfdf);
}

nav li a:hover {
    color: var(--bg-color-background, #23232d);
}

.message-list li .name,
.message-list li .subject {
    color: #fff;
}

.message-list li:hover {
    background-color: var(--bg-color-background, #23232d);
}

.message-list li:hover .misc {
    background-color: var(--bg-color-background, #23232d);
}

.message-list li:hover .misc::before {
    background-image: none;
    background-color: var(--bg-color-background, #23232d);
}

ul li:hover {
    color: var(--text-color-primary, #dfdfdf)
}

button,
.button {
    color: #000;
}

body,
input,
textarea {
    color: var(--text-color-primary, #dfdfdf)
}

div.popup.select > div {
    background-color: var(--bg-color-background, #23232d);
}

div.popup.select > div.hover:not(.disabled):not(.separator) {
    background-color: var(--text-color-primary, #dfdfdf);
    color: #000;
}

div.popup {
    border: 1px solid var(--bg-color-background, #23232d);
    background-color: var(--bg-color-background, #23232d);
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
}

div.popup.select > div.active::before {
    border-left-color: var(--text-color-primary, #dfdfdf);
}

header #user button {
    color: var(--text-color-primary, #dfdfdf);
}

.message-list .misc {
    background-color: var(--bg-color-lighter, #2c2c36);
    ;
}

.message-list .misc::before {
    background-image: none;
    background-color: var(--bg-color-lighter, #2c2c36);
    ;
}

.message-list li.unread .name,
.message-list li.unread .subject {
    color: #fc6722;
}

.popover__bubble,
.skin-default .popover__bubble {
    background-color: var(--bg-color-background, #23232d);
}

.user-menu-popover .menu__item--logout {
    color: var(--text-color-primary, #dfdfdf);
}

#detail .body {
    color: #000;
    background-color: #fefefe;
    padding-left: 72px;
    margin-left: 0px;
    padding-top: 25px;
    padding-bottom: 25px;
}

.notification {
    background-color: var(--text-color-hover, #3b3b4b);
    color: var(--text-color-primary, #dfdfdf);
}

#detail .info {
    background-color: #f5f4f3;
    color: var(--bg-color-background, #23232d);
}

.compose-full .editor .area {
    color: #000;
}

.compose-full .editor {
    background-color: #f5f4f3;
}

button.plain {
    color: var(--text-color-primary, #dfdfdf);
}

button.popup.open:not(.invert) {
    color: var(--text-color-hover, #3b3b4b);
}

.compose-full dl > div.subject .misc {
    display: none;
}

.message-list li.narrow .attachment,
.message-list .attachment span,
.message-list .attachment svg {
    color: #fc6722;
}

.paper {
    background-color: var(--bg-color-background, #23232d);
}

.widget-vcard {
    background-color: var(--text-color-hover, #3b3b4b);
}

nav li a:hover {
    background-color: var(--text-color-hover, #3b3b4b);
    color: var(--text-color-primary, #dfdfdf);
}

.person-list li a {
    color: var(--text-color-primary, #dfdfdf);
}

.person-list li a:hover {
    background-image: none;
    background-color: var(--text-color-hover, #3b3b4b);
    color: var(--text-color-primary, #dfdfdf);
}

.compose-full {
    background-color: var(--bg-color-background, #23232d);
}

#detail.has-sky .im-sky {
    display: none;
}




/** Columns */
.article {
    padding: 20px;
}

.article:nth-child(odd) {
    background-color: var(--bg-color-background, #23232d);
}

.article:nth-child(even) {
    background-color: var(--text-color-hover, #3b3b4b);
}

.article__title {
    font-size: 1.5em;
}




/** Results */
.SearchForm-button {
    color: #f13f3f;
}

.FixerNews .BoxTitle {
    color: #9b9bb0;
}

.Logo.Logo--small .Logo-container-link--full {
    top: -1.5rem;
    left: -14.6rem;
    background-color: var(--text-color-hover, #3b3b4b);
    border-radius: 15px;
    padding: 10px;
}

.FixerNews-item-text-title.Board-label-title, .Trending-link {
    color: #669bf0;
}


.Result-title-link {
    color: #9b9bb0;
}

.Result-description,
.Result-description--news {
    color: var(--text-color-primary, #dfdfdf);
}

.AnchorList .AnchorList-list .AnchorList-link {
    color: #f13f3f;
}

.PageWrapper {
    background-color: var(--bg-color-background, #23232d);
}

.ResultContainer-header-query {
    color: #f13f3f;
}

.ResultContainer-footer-link {
    color: #f13f3f;
}

.RelatedResults-list-item-link {
    color: #f13f3f;
}

.RelatedResults-title {
    color: #9b9bb0;
}

.ResultContainer-header-service {
    color: #9b9bb0;
}

.Paging-link {
    color: var(--text-color-primary, #dfdfdf);
}

.Breadcrumbs-link,
.Result-url,
.Result-url-link,
.Result-url-link:active,
.Result-url-link:visited {
    color: #f13f3f;
}

.Footer-link {
    color: var(--text-color-primary, #dfdfdf);
}
}
