css:
@-moz-document domain("takealot.com") {
:root {
    --bg-color: #151515;
    --sold-out-bg-color: #960000;
    --header-bg-color: #01314e;
    --input-bg-color: #151515;
    --hover-bg-color: #282828;
    --color: #999;
    --light-color: #ccc;
    --dark-color: #111;
    --hover-shadow: 0 0 2px 0 #fff;
    --border: 1px solid #282828;
    
    /*  Icon was generated at www.flaticon.com  */
    --img-angle-down: url(data:image/svg+xml;utf8;base64,);
}
html, body, .rap.main, .scroll-bar, .listing-filters {
    background: none;
    background-color: var(--bg-color) !important;
    color: var(--color) !important;
}
div, p, span, ul, h1, h2, h3, a {
    color: var(--color) !important;
}
div.header, div.footer {
    background-color: var(--bg-color);
}
div.rap.main {
    position: relative;
}
div.content {
    position: relative;
    left: 0;
    top: 0;
    width: 100% !important;
    margin: 0 !important;
    float: none;
}
.cat-home div.content, .search-double div.content {
    left: 17%;
    top: 20px;
    width: 70% !important;
}
div.promo-bar, div.deals-header {
    display: none;
}
div.rap.main > div.sb.sb-l {
    position: absolute;
    left: 0;
    top: 12px;
    margin: 0;
    float: none;
    width: 15%;
}
div.rap.main > div.sb.sb-r {
    display: none;
}
div.search-nav-wrap, div.footer-logos, div.trim {
    background: none;
    background-color: var(--header-bg-color) !important;
}
div.cat-navigation {
    background-color: var(--input-bg-color);
    box-sizing: border-box;
}
div.cat-navigation:hover {
    box-shadow: var(--hover-shadow);
}
input:not(.submit), select {
    background: none !important;
    background-color: var(--input-bg-color) !important;
    color: var(--color) !important;
}
input.submit {
    background-color: var(--input-bg-color) !important;
    border: none !important;
    margin-left: -44px !important;
}
input#search {
    padding-right: 40px;
}
input[type="text"]:hover {
    box-shadow: var(--hover-shadow) !important;
}
input[type="text"]:focus {
    box-shadow: var(--hover-shadow) !important;
}
label[for="search"]:after {
    background-color: var(--input-bg-color) !important;
    border: 1px solid var(--color) !important;
    height: 28px;
}
label.custom-select {
    background: none;
    border: 1px solid var(--hover-bg-color);
    cursor: pointer;
}
label.custom-select select {
    cursor: pointer;
}
label.custom-select:after {
    background: none;
    background-image: var(--img-angle-down) !important;
    background-size: 40%;
    background-position: center !important;
    background-repeat: no-repeat;
    right: 1px;
    top: 1px;
    border-left: 1px solid var(--hover-bg-color);
}
label.custom-select:hover {
    background-color: inherit !important;
    box-shadow: var(--hover-shadow);
}
label.custom-select:hover:after {
    background-color: inherit !important;
}
form#search_mini_form label {
    position: relative;
    position: absolute;
}
form#search_mini_form label:after {
    background: none;
    background-image: var(--img-angle-down) !important;
    background-size: 40%;
    background-position: center !important;
    background-repeat: no-repeat;
    box-sizing: border-box;
    border: none !important;
    margin-left: 168px;
}
form#search_mini_form label:before {
    display: none;
}
form#search_mini_form #search-type {
    width: 200px;
    padding-right: 32px;
}
form#search_mini_form #search-type:hover {
    box-shadow: var(--hover-shadow) !important;
}
div.sub-cat-wrap a {
    background-color: var(--input-bg-color) !important;
}
td.topnav > a {
    border-left: 1px solid var(--hover-bg-color);
}
td.topnav > a:hover, td.nav-dept-link a:hover {
    background-color: var(--hover-bg-color) !important;
    color: var(--light-color) !important;
}
div.filter-paginator a.page-current {
    background: none !important;
    background-color: var(--hover-bg-color) !important;
    color: var(--light-color) !important;
}
div.filter-paginator a {
    background: none !important;
    background-color: var(--bg-color) !important;
}
div.filter-paginator a:hover {
    box-shadow: var(--hover-shadow) !important;
}
div.listing-filters.narrow-sort, ul.category.breadcrumb, .product-list > li.result-item {
    border-bottom: var(--border);
}
div.p-data {
    border-right: var(--border) !important;
}
div.slideshow, div.brands-strip {
    display: none;
}
li.product-list-item {
    border: var(--border) !important;
}
li.product-list-item:hover {
    border: none !important;
    box-shadow: var(--hover-shadow);
}
ul.dropdown-menu {
    background: var(--bg-color) !important;
    border: var(--border) !important;
    height: auto !important;
}
li.menuitem {
    height: 1.8em;
    line-height: 1.8em !important;
    vertical-align: middle
}
li.menuitem.daily-deals {
    position: relative !important;
}
abbr.currency, span.amount, .price > span {
    color: #a52a2a !important;
}
.deal-heading {
    color: var(--color) !important;
}
.deal-qty > span {
    color: goldenrod !important;
}
.deals-nav {
    border-bottom: var(--border);
    margin-bottom: 0;
}
.deals-nav-tabs a {
    background: none;
    background-color: inherit;
    border: none;
}
.deals-nav-tabs a.active {
    background-color: var(--hover-bg-color);
    color: #ccc !important;
}
.deals-nav-tabs a:hover {
    background-color: var(--hover-bg-color);
    color: #ccc !important;
    border: none;
}
.deal-image:after {
    background: radial-gradient(transparent 50%, #0006);
    content: '';
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    right: 0;
    opacity: 1;
    transition: opacity 500ms;
}
.daily-deal-item:hover .deal-image:after, .daily-deal-item:hover .badge.sold-out:after {
    opacity: 0;
    transition: opacity 200ms;
}
.daily-deal-item {
    border: var(--border);
}
.daily-deal-item:hover {
    border: none;
    box-shadow: var(--hover-shadow);
}
.deal-timer {
    height: unset;
    max-height: 40px;
    border-bottom: none;
}
.paginating {
    margin: 0;
}
.the-deals > .paginating {
    border: none;
}
.ribbon-wrapper {
    width: 100%;
    height: 100%;
    pointer-events: none;
}
.badge {
    height: 35px;
    width: 35px;
    line-height: 35px;
    font-size: 12px;
}
.badge.tops {
    background: none;
    background-color: #228b22;
}
.badge.sold-out {
    background: none;
    background-color: var(--sold-out-bg-color) !important;
}
.badge.sold-out:after {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    background-color: rgba(0, 0, 0, 0.6);
    width: 100%;
    height: 100%;
    opacity: 1;
    transition: opacity 500ms;
}
.badge b, .badge span, .my-saving {
    color: var(--dark-color) !important;
}
h2 > span {
    background: var(--bg-color) !important;
    background-color: inherit;
}
.pretty-row, .row .product-list {
    display: flex;
    justify-content: space-evenly;
}
.pretty-list {
    flex: 0 0 auto;
    margin: 0 !important;
}
.nav-panel {
    background: none !important;
    background-color: var(--bg-color) !important;
}
.nav-spot {
    display: none;
}
div.widget--search h3 {
    border-bottom: var(--border) !important;
}
div.widget--search li {
    border-bottom: var(--border) !important;
}
.acc-sub-nav {
    background: none;
    background-color: var(--bg-color) !important;
}

/************************************************************************
***     Links & Buttons
************************************************************************/
a.close {
    background: none;
    text-indent: inherit;
    font-size: 16px !important;
    color: #9e1111 !important;
    text-align: center;
}
a.close:hover {
    background: none;
    text-shadow: 0 0 2px #b13a3a;
    text-decoration: none;
    box-shadow: 0 0 2px 0 #b13a3a;
}
a.btn-size {
    background: none !important;
    border: none;
}
a.view-more {
    background: none !important;
    border: var(--border) !important;
}
a.view-more:hover {
    background-color: var(--hover-bg-color) !important;
}
a.btn-size:hover, .btn-atc:hover {
    text-decoration: underline;
}
a.track {
    color: lightseagreen !important;
}
.app-link a {
    background: none !important;
    border: var(--border) !important;
}
.app-link a:hover {
    background: var(--hover-bg-color) !important;
}
option {
    background: none;
    background-color: var(--bg-color);
}
option:hover {
    background: none;
    background-color: inherit;
    box-shadow: 0 0 10px 100px var(--hover-bg-color) inset;
}
#nav li.menuitem:hover {
    background: none;
    background-color: var(--hover-bg-color);
}
}
