css:
@-moz-document domain("schoology.com") {
/*----------------------------Made-by-yours-truly-Marshy----------------------------*/

:root {
    --main-color: #5675ee;
    --box-shadow: #dddddd;
    --main-color-box-shadow: #4766da;
    --anime: bounce 1500ms linear both;
    --anime-reverse: bounce-reverse 1500ms linear both;
}

._1SIMq {
	background-color: var(--main-color) !important;
	box-shadow: 0 5px var(--main-color-box-shadow);
}

.Header-header-button-1EE8Y {
	transition: .6s;
	color: #fff;
}

.Header-header-button-1EE8Y:hover {
	background-color: rgba(255,255,255,.05) !important;
}

._2ue1O {
	border-top-style: none;
	border-top-width: 0px;
}

.s-enable-course-dashboard.theme-2016-layout #home-feed-container {
	box-shadow: 0 4px 0px 0 var(--box-shadow);
	background: #fff;
	padding: 10px;
	border-radius: 3px;
}

body {
	background: #e9e9e9;
	color: #717171;
	font: 12px "Sgy Open Sans","Lucida Grande",Tahoma,Verdana,Arial,sans-serif;
	text-wrap: normal;
}

.s-enable-course-dashboard.theme-2016-layout #main {
	background: transparent !important;
	box-shadow: none !important;
}

.theme-2016-layout #sidebar-left .active-trail a, .theme-2016-layout #main, .theme-2016-layout #right-column-inner > div:not(:empty) {
	box-shadow: 0 4px 0px 0 var(--box-shadow);
	border-radius: 3px;
}

body a, body .clickable, body .smart-box .filter-block li a, body #content-left-top .item-list li a, body .popups-tab, body .enrollment-filters span, body .grading-groups-list .grading-group, body #grading-group-create:hover {
	color: var(--main-color);
    text-decoration: none !important;
}

body .smart-box .filter-block li .smartbox-boxtab, body .smart-box .filter-block li .personal-blog a {
	color: var(--main-color);
    text-decoration: none !important;
}

._2P7CY:focus, ._3HOk8:hover, ._37jp-:active, ._37jp-:active:focus, .les2- {
	border-color: transparent !important;
    box-shadow: 0 3px var(--box-shadow) !important;
    border-radius: 3px !important;
}

.Card-card-image-uV6Bu {
	background: transparent no-repeat 50%/100%;
	height: 10rem;
	border-radius: 4px 4px 0 0;
}

._1LY8n {
	display: block;
	outline-color: transparent !important;
}

.Card-card-data-17m6S {
	height: 8rem;
	background-color: rgba(0,0,0,.05) !important;
	border-radius: 0 0 4px 4px;
}

.uQOmx {
	-ms-grid-row-span: 1;
	grid-row-end: span 1;
	border: none !important;
	border-radius: 0px !important;
	outline-color: var(--main-color) !important;
}

.Header-header-button-active-state-3AvBm:hover:not(:active) {
	background-color: var(--main-color);
	color: #fff;
}

.Header-header-drop-menu-item-3d3IZ {
	color: var(--main-color);
    transition: .4s;
}

._1ui-c {
	border-top-width: 0px;
}

.sgy-tabbed-navigation li {
	display: block;
	float: left;
	text-transform: uppercase;
	padding: 0 30px 5px;
	padding: 8px;
}

.sgy-tabbed-navigation li.active {
	/* border-bottom: 5px solid #556370; */
	background-color: rgba(0,0,0,.05);
	padding: 8px;
}

.sgy-tabbed-navigation {
	display: block;
	width: calc(100% - -5px);
	max-width: 990px;
	height: 25px;
	list-style-type: none;
	padding-top: 5px;
	margin: 0;
	margin-bottom: 20px;
	border-bottom: 0px solid #c1c7cb;
}

.sgy-tabbed-navigation li.active {
	border-bottom: 0px solid #556370;
}

._2Y5fB {
	border-radius: 3px;
}

._1rsSg:active, ._2WKzc:hover:not(:active), ._3U8Br, ._21bOr:focus:not(:active) {
	color: var(--main-color);
    text-decoration: none !important;
}

.fc-border-separate th, .fc-border-separate td {
	border-width: 0px 0 0 0px;
}

.fc-widget-header, .fc-widget-content {
	border: 0px solid #ccc;
}

body .fc-border-separate thead th {
	border-width: 0 0 0px !important;
}

.fc-border-separate th.fc-last, .fc-border-separate td.fc-last {
	border-right-width: 0px;
}

.menuopt-wrapper {
	padding: 10px 40px 10px 30px;
	border: 0px solid #ddd;
}

body .fc-state-default {
	border: 0px solid #c7c7c7;
}

._2SVA_ {
	background: url(https://ui.schoology.com/design-system/assets/schoology-logo-horizontal-white.884fbe559c66e06d28c5cfcbd4044f0e.svg) no-repeat !important;
	top: 14px;
	position: relative;
	margin-left: 10px;
	margin-right: 10px;
}

#edge-filters-menu {
	min-width: 150px;
	z-index: 1;
	right: 0;
	top: 21px;
	display: none;
	position: absolute;
	background: #fff;
	border: 0px solid #333;
	padding: 5px 0 0 0;
	box-shadow: 0 3px 0px var(--box-shadow);
}

.sgy-card {
	background: #fff;
	border: 0px solid #fff;
	border-radius: 2px;
	box-shadow: 0 4px 0px 0 var(--box-shadow) !important;
	outline: none !important;
}

#smart-box-content {
	position: relative;
	margin-top: 15px;
}

body #wrapper .infotip.hide-qmark {
	cursor: pointer;
	top: 19px;
}

body .submit-span-wrapper, body .click-submit, body .submit-btn, body .popups-body .submit-span-wrapper, body #nav .s-notifications-mini .requester-links a:hover {
	background-image: none;
	border-color: var(--main-color);
	background-color: var(--main-color);
    border-radius: 2px;
    box-shadow: 0 3px var(--main-color-box-shadow);
}

