css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("energeticsynthesis.com") 
{



#g-top {
	background: linear-gradient(to bottom, #000, #126986);
}

#g-navigation {
	background: rgba(0, 0, 0, 0.9);
	color: #0368ff;
	position: relative;
	z-index: 10002;
}

#g-page-surround {
	background: #000;
	overflow: hidden;
}

#g-above {
	background: #0e0e0e;
	color: #000;
}

.moduletable.blog-ticker-mod {
	overflow: hidden;
	background-color: #0f0f0f;
	margin: 0 -1rem;
	border-radius: 0.625rem;
	box-shadow: inset 1px 1px 7px 3.5px #e4e4e4;
}

#g-above {
	color: #001ef9;
}

#g-showcase {
	background: #000;
	color: #888;
	position: relative;
	z-index: 10003;
}

#g-footer {
	background: linear-gradient(to bottom, #000, #125873);
}

#g-bottom {
	background: linear-gradient(to bottom, #000, #045872);
	border-radius: 0;
}
#g-sidebar {
	background: #171717;
	color: #000;
}

#g-navigation .g-main-nav .g-dropdown {
	background: rgba(0, 0, 0, 0.9);
	border: 2px solid #0078ff;
	border-radius: 4px;
}
#g-navigation .g-main-nav .g-sublevel > li:hover > .g-menu-item-container, #g-navigation .g-main-nav .g-sublevel > li.active > .g-menu-item-container {
	border-bottom: 1px solid #0093ff;
	color: #0093ff;
}
h1, h2, h3, h4, h5 {
	color: #0078ff;
}
a {
	color: #0093ff;
	-webkit-transition: all 0.2s;
	-moz-transition: all 0.2s;
	transition: all 0.2s;
}
#g-utility.breadcrumb-area {
	background-position: none;
	background-size: cover;
	margin: 0;
	background-color: rgba(0, 0, 0, 0.9) !important;
	color: #008aff !important;
}
.side-menu .menu li:hover {
	background-color: #000;
	color: #009cff;
}
.side-menu .menu li:hover a {
	color: #0078ff;
}
body {
	color: #b3b3b3;
}
div.spacer.product-container {
	border-radius: 1em;
	padding: 1em 0.5em;
	background-color: #000;
	margin: 0.5em;
	box-shadow: inset 1px 1px 6px 1px #ddd;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
	background-color: #141414;
	border: 1px solid rgba(0, 111, 255, 0.9);
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
	background-color: #141414;
	border: 1px solid rgba(0, 111, 255, 0.9);
}
#g-navigation .g-main-nav .g-toplevel > li:hover > .g-menu-item-container, #g-navigation .g-main-nav .g-toplevel > li.active > .g-menu-item-container {
	color: #fff;
}
.cat-children > div {
	background: rgba(32, 32, 32, 0.9); 
 }
.moduletable.sidea {
	background-color: rgba(0, 0, 0, 0.9);
}

.moduletable.st42-paypal {
	background-color: rgba(0, 0, 0, 0.9);
	color: #0bf;
	font-weight: bold;
}

.latest-home .g-array-item-read-more {

	background: #000;
}

textarea:focus, input[type="text"]:focus, input[type="password"]:focus, input[type="datetime"]:focus, input[type="datetime-local"]:focus, input[type="date"]:focus, input[type="month"]:focus, input[type="time"]:focus, input[type="week"]:focus, input[type="number"]:focus, input[type="email"]:focus, input[type="url"]:focus, input[type="search"]:focus, input[type="tel"]:focus, input[type="color"]:focus, .uneditable-input:focus {
	box-shadow: inset 0 1px 1px rgba(255, 0, 0, 0), 0 0 8px rgba(0, 198, 255, 0.9);
}

.g-search input[type="text"], .g-search input[type="search"] {

	color: #00aeff;
}
#g-above {
	color: #0064f9;
	background-color: #000;
}
.moduletable.blog-ticker-mod {
	box-shadow: inset 1px 1px 7px 3.5px #00b7ff;
}

}
