css:
@-moz-document domain("sleeping.town"), domain("glitch.social"), domain("cybre.space"), domain("mastodon.social"), domain("toot.cat"), domain("botsin.space"), domain("chitter.xyz") {
/*supports vanilla mastodon front end, sleeping town front end, and glitch-soc front ends
if this CSS seems awful (it is) is mostly cause of that and selectively overriding mismatched silliness
*/
body,
.icon-button,
.text-icon-button,
.getting-started p,
.status-card__title,
.composer--upload_form--item > div input,
.column-header__button {
    color: #fff1e8;
}
.composer--options--dropdown--content--item .fa-unlock-alt,
.status__visibility-icon.fa-unlock-alt {
    color: #008751;
}
.notification__favourite-icon-wrapper .fa.star-icon,
.star-icon.active {
    color: #ffec27;
}
.composer--options--dropdown--content--item .fa-globe,
.status__visibility-icon.fa-globe,
.column-header > .column-header__back-button,
.account__header__fields a {
    color: #29adff;
}
.button {
    background-color: #7E2553;
    color: #fff1e8;
}
  .focusable:focus {
	outline: 0;
	background: #37322e;
}
.icon-button.inverted,
.text-icon-button:active,
.text-icon-button:focus,
.text-icon-button:hover,
.column-header__button:hover {
    color: #c2c3c7;
}
.column-link__badge,
.column-inline-form,
.setting-text {
    background: #5f574f;
    color: #fff1e8;
}

.drawer {
    padding-top: 10px;
    padding-bottom: 0;
    flex: 0 0 280px;
}
.drawer__tab {
    color: #fff1e8;
}
.column,
.drawer {
    padding: 0 !important;
    margin: 10px 5px 0 5px;
    overflow: auto;
    border-radius: 6px;
}
.drawer__inner__mastodon {
    background: transparent url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 234.80078 31.757813" width="234.80078" height="31.757812"><path d="M19.599609 0c-1.05 0-2.10039.375-2.90039 1.125L0 16.925781v14.832031h234.80078V17.025391l-16.5-15.900391c-1.6-1.5-4.20078-1.5-5.80078 0l-13.80078 13.099609c-1.6 1.5-4.19883 1.5-5.79883 0L179.09961 1.125c-1.6-1.5-4.19883-1.5-5.79883 0L159.5 14.224609c-1.6 1.5-4.20078 1.5-5.80078 0L139.90039 1.125c-1.6-1.5-4.20078-1.5-5.80078 0l-13.79883 13.099609c-1.6 1.5-4.20078 1.5-5.80078 0L100.69922 1.125c-1.600001-1.5-4.198829-1.5-5.798829 0l-13.59961 13.099609c-1.6 1.5-4.200781 1.5-5.800781 0L61.699219 1.125c-1.6-1.5-4.198828-1.5-5.798828 0L42.099609 14.224609c-1.6 1.5-4.198828 1.5-5.798828 0L22.5 1.125C21.7.375 20.649609 0 19.599609 0z" fill="%23282C37"/></svg>') no-repeat bottom/100% auto;
}
.drawer__inner {
    background: none !important;
}
.drawer--header,
.drawer__header {
    font-size: 16px;
    filter: drop-shadow(0px 3px 3px #000);
    border-radius: 6px;
}
.column-header {
    filter: drop-shadow(0px 3px 3px #000);
}
.column-header,
.column-header__button,
.compose-form .compose-form__buttons-wrapper,
.drawer--header,
.drawer__header {
    background: #1D2B53;
}
.column {
    box-shadow: 3px 4px 9px 0px #000;
    min-width: 260px !important;
    max-width: 400px !important;
    flex: 1 1 auto !important;
    filter: initial;
    background: rgba(0, 0, 0, .6);
}
.empty-column-indicator,
.error-column {
    color: #c2c3c7;
    background: none;
}
.columns-area {
    display: flex;
    flex-direction: row;
}
.drawer.mbstobon-0 .contents,
.drawer.mbstobon-1 .contents,
.drawer.mbstobon-2 .contents,
.drawer.mbstobon-3 .contents {
    filter: initial;
    background: none;
}
.column > .scrollable {
    background: none;
}
.column-back-button,
.column-header__back-button {
    background: #1d2b53;
    color: #29adff;
}
.detailed-status__action-bar,
.notification-follow,
.status {
    border-bottom-width: 8px;
    border-bottom-color: #1b1816;
}
.muted .status__content a,
.muted .status__content p,
.muted .status__display-name strong {
    color: #948E86;
}
.muted .status__content .status__content__spoiler-link {
    background: #5f574f;
    color: #948E86;
    
}
.icon-button {
    transition: all .1s;
    color: #c2c3c7;
    filter: drop-shadow(1px 1px 0px #000);
}
.icon-button:active,
.icon-button:focus,
.icon-button:hover {
    color: #c2c3c7;
}
.icon-button.disabled {
    color: rgba(0, 0, 0, .6);
    cursor: default;
    filter: none;
}
.composer--options {
    background: #1d2b53;
}
.drawer > .contents {
    filter: drop-shadow(0px 4px 4px #000);
}
.ui {
    background-color: #5f574f;
}
.drawer--search input,
.search__input {
    color: #000;
    background: #fff1e8;
}
.drawer--search > .icon {
    color: #000;
}
.search__icon .fa-times-circle {
    color: #000;
}
.drawer--search input:focus,
.search__input:focus {
    background: #fff1e8;
}
.search-popout {
    background: #fff1e8;
}
.search-popout > * {
    color: #5f574f;
}
.search-popout h4,
.search-popout em {
    color: #000;
}
.search-results {
    background: rgba(0, 0, 0, .9);
    color: #c2c3c7;
    box-shadow: 0px 3px 6px 0px #000;
}
.search-results__header {
    color: inherit;
    background: #000;
}
.search-results__section h5 {
    background: none;
}
.account {
    padding: 10px;
    border-bottom: 1px solid #393f4f;
}
.column-subheading {
    background: #1e1c19;
    color: #c2c3c7;
}
.column-link {
    color: #fff1e8;
    background: none;
}
.column-link:hover {
    background: #1d1a17;
}
.getting-started {
    background: #1e1c19;
    color: #c2c3c7;
}
.getting-started__wrapper,
.getting_started {
    background: none;
}
.getting-started a {
    color: #c2c3c7;
}
.composer,
.compose-form {
    filter: drop-shadow(1px 3px 3px #000);
}
.composer--spoiler input,
.composer--textarea > label .textarea,
.compose-form .autosuggest-textarea__textarea,
.compose-form .spoiler-input__input {
    background: #fff1e8;
    color: #000;
}
.composer--reply,
.reply-indicator {
    margin: 0 0 -2px;
    border-radius: 4px 4px 0 0;
    padding: 10px;
    background: rgba(0, 0, 0, .5);
    color: #c2c3c7;
}
.composer--reply > .content,
.reply-indicator__header,
.reply-indicator__content,
.reply-indicator__display-name {
    color: #c2c3c7;
}
.composer--options .icon-button,
.composer--options .text-icon-button {
    color: #fff1e8;
    opacity: 1;
}
.icon-button:active,
.icon-button:focus,
.icon-button:hover {
    color: #5f574f;
}
.detailed-status {
    background: #37322e;
}
.detailed-status__action-bar {
    background: #1d1a17;
    border-top: none;
}
.status__content .status__content__spoiler-link {
    transition: all .1s;
    background: #5F574F;
    box-shadow: 0px 1px 2px 0 #000;
    color: #fff1e8;
}
.status__content .status__content__spoiler-link:hover {
    background: #5f574f;
    text-decoration: none;
    box-shadow: 0px -1px 1px 0 #000;
    transform: translateY(1px);
}
.account__header,
.account__header__wrapper {
    background-color: rgb(0, 0, 0, .3);
}
.account__header .account__header__fields dt {
    color: #fff1e8;
    background: #5f5f4f;
}
.account__header .account__header__fields dd {
    color: #fff1e8;
    background: #1d1a17
}
.account__action-bar__tab > span {
    color: #fff1e8;
}
.account__action-bar__tab strong,
.status__prepend,
.status__prepend .status__display-name strong,
.status__relative-time {
    color: #c2c3c7;
}
.account--panel {
    background: #1d1a17;
    border-top: none;
    border-bottom: 3px solid #000;
}
.account--panel .fa-check {
    color: #00e436;
}
.account--panel .fa-times {
    color: #ff004d;
}
.status.status-direct {
    background: #5f574f;
    color: #fff1e8;
}
.status-card {
    border: 1px solid #000;
    background: #4a443e;
}
.status-card:hover {
    background: #24211e;
}
.navigation-bar,
.drawer--account {
    color: #c2c3c7;
    background: rgb(0, 0, 0, .6);
}
.compose-form {
    padding: 5px;
}
.compose-form .compose-form__modifiers {
    background: #fff1e8;
}
.composer--upload_form,
.compose-form .compose-form__modifiers .compose-form__upload-wrapper {
    color: #fff1e8;
    background: rgba(0, 0, 0, .45);
}
.composer--upload_form--item > div > .close {
    mix-blend-mode: difference;
    color: #fff1e8;
}
.composer--publisher > .count {
    font-size: 14px;
    font-weight: 500;
}

.compose-form .compose-form__buttons-wrapper .character-counter__wrapper .character-counter {
    color: #fff1e8;
    font-size: 14px;
}
.composer--upload_form--item > div input::placeholder {
    opacity: .9;
    color: #c2c3c7;
}
input::placeholder {
    color: #000;
    opacity: .75;
}
}
