css:
@-moz-document domain("mybroadband.co.za") {
a:not([class^="fauxBlockLink"]):not([class^="subNodeLink"]) {
    color: #c2c2c2;
}
.contentRow-main > a {
    color: black;
}
.p-footer,
.p-navSticky {
    max-width: 85% !important;
}
.p-pageWrapper,
body {
    color: #c2c2c2 !important;
    max-width: 100% !important;
}
.site-footer {
    width: 85% !important;
    margin: auto;
}
#mainNav ul li a {
    color: #c2c2c2;
}
.p-pageWrapper,
body,
.p-nav,
.p-sectionLinks {
    color: #c2c2c2;
    background-color: #0f0f0f !important;
}
.message-inner,
.message-cell.message-cell--user,
#mainNav {
    color: #c2c2c2;
    background-color: black !important;
}
.message-cell.message-cell--user,
.message-cell.message-cell--action {
    position: relative;
    border-right: 2px solid #4F4E4C !important;
}
.p-body-inner {
    max-width: 90% !important;
}
.bbCodeBlock-title {
    color: #fff !important;
    background: #636363 !important;
}
.bbCodeBlock {
    color: #fff !important;
    background: black;
    border: 1px solid #4F4E4C !important;
    border-left-color: #4F4E4C !important;
    border-left: 3px solid #4F4E4C !important;
}
.fr-wrapper::-moz-selection {
    color: #c2c2c2;
    background: #e99d91 !important;
    background-color: rgb(233, 157, 145)!important;
}
.block-filterBar {
    border-bottom: 1px solid #000 !important;
}
.likesBar {
    background-color: black;
}
[class^="block"],
.structItemContainer,
.structItem {
    background: black !important;
}
.p-footer,
.p-navSticky,
.p-sectionLinks {
    width: 100% !important;
    background-color: black;
}
.tabs--standalone {
    background: #cecece1a !important;
}
.pageNav-jump,
.pageNav-page--earlier,
.pageNav-page--current,
.pageNav-page,
.formSubmitRow-bar {
    color: #c2c2c2;
    background: black !important;
}
[class^="pageNav-page"]:hover {
    filter: opacity(80%);
}
#top_menu {
    filter: invert(0%);
    background: black;
}
[class^="p-navEl"] {
    color: #c2c2c2 !important;
    background-color: inherit !important;
}
[class^="structItem"] {
    color: #c2c2c2;
}

[class^="structItem-title"]  {
    font-size: 17px;
    font-weight: 100;
}

[class^="structItem-pageJump"] > a {
    background-color: transparent;
}
[class^="structItem-pageJump"] > a:hover {
    border-color: yellow;
    background-color: transparent;
    color: yellow;
}
.actionBar-action {
    color: #c2c2c2 !important;
}
.fr-element {
    background-color: white;
    color: #c2c2c2;
    width: 100%;
    min-height: 200px !important;
    height: auto;
    display: block;
    resize: vertical;
    box-sizing:border-box;
}
.fr-element > p {
    color: black;
}
.fr-wrapper > p {
    color: black;
    background-color: grey;
}
.fr-toolbar,
.input {
    filter: invert(15%);
    border-top: #2d2d2d;
}
.button--link,
.menu-linkRow,
.pika-single {
    filter: invert(10%);
}
body {
    overflow-y: unset !important;
}
.pairs > dd {
    color: #c2c2c2;
}
.button-text,
.button,
.button.button,
.button--icon,
a.button--link,
a.button,
a.button.button {
    color: #c2c2c2;
    background-color: black;
    border-color: black;
    background: black;
}
}
