css:
@-moz-document domain("socialblade.com") {
    :root {
        --site-background: hsl(0, 0%, 13%);
        --container-background: hsl(0, 0%, 20%);
        --container-content: hsl(0, 0%, 27%);
        --container-highlight: hsl(0, 0%, 35%);
        --graph-line: hsl(0, 0%, 47%);
        --font-color: hsl(0, 0%, 93%);
        --font-color-darker: hsl(0, 0%, 69%);
        --site-header-color: rgb(47, 44, 44);
        --button-border-color: var(--site-background);
        --text-shadow-color: var(--site-background);
        --site-color: /*[[socialblade-color]]*/;
        --good: #6f0;
        --bad: #ff4200;
    }

    :root {
        --socialblade-color: /*[[socialblade-color]]*/;
        --youtube-color: /*[[youtube-color]]*/;
        --twitch-color: /*[[twitch-color]]*/;
        --twitter-color: /*[[twitter-color]]*/;
        --instagram-color: /*[[instagram-color]]*/;
        --facebook-color: /*[[facebook-color]]*/;
        --dailymotion-color: /*[[dailymotion-color]]*/;
        --mixer-color: /*[[mixer-color]]*/;
        --dlive-color: /*[[dlive-color]]*/;
        --storyfire-color: /*[[storyfire-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/youtube/.*"),
regexp("https?://socialblade.com/favorites/youtube") {
    :root {
        --site-color: /*[[youtube-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/twitch/.*"),
regexp("https?://socialblade.com/favorites/twitch") {
    :root {
        --site-color: /*[[twitch-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/twitter/.*"),
regexp("https?://socialblade.com/favorites/twitter") {
    :root {
        --site-color: /*[[twitter-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/instagram/.*"),
regexp("https?://socialblade.com/favorites/instagram") {
    :root {
        --site-color: /*[[instagram-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/facebook/.*"),
regexp("https?://socialblade.com/favorites/facebook") {
    :root {
        --site-color: /*[[facebook-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/dailymotion/.*"),
regexp("https?://socialblade.com/favorites/dailymotion") {
    :root {
        --site-color: /*[[dailymotion-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/mixer/.*"),
regexp("https?://socialblade.com/favorites/mixer") {
    :root {
        --site-color: /*[[mixer-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/dlive/.*"),
regexp("https?://socialblade.com/favorites/dlive") {
    :root {
        --site-color: /*[[dlive-color]]*/;
    }
}

@-moz-document regexp("https?://socialblade.com/storyfire/.*"),
regexp("https?://socialblade.com/favorites/storyfire") {
    :root {
        --site-color: /*[[storyfire-color]]*/;
    }
}

@-moz-document domain("socialblade.com") {
    :root {
        --site-color: /*[[platformOverride]]*/;
    }
}

@-moz-document domain("socialblade.com") {
    /*--------------------------------------------------*/
    /* General Background, Site Header and Footer       */
    /* Header Menus, Cookie Settings, 404, Cloudflare   */
    /* Graphs                                           */
    /*--------------------------------------------------*/
    body {
        color: var(--font-color) !important;
        background: var(--site-background) !important;
    }

    a,
    a:hover {
        text-decoration: none;
        color: var(--site-color);
    }

    p,
    span,
    div,
    h2,
    label,
    input,
    a,
    option,
    select,
    button {
        text-shadow: -1px -1px 0 var(--text-shadow-color), 1px -1px 0 var(--text-shadow-color), -1px 1px 0 var(--text-shadow-color), 1px 1px 0 var(--text-shadow-color);
    }

    .hint--top {
        color: var(--site-color) !important;
    }

    /* Substitute colors */
    *[style*="color:#41a200;"] {
        color: var(--good) !important;
    }

    *[style*="color:#e53b00;"] {
        color: var(--bad) !important;
    }

    /* Site Header */
    #top-header,
    #top-header-wrap,
    #footer-bar-wrap,
    #footer-bar-wrap div,
    #footer-main-outer-wrap,
    #footer-main-outer-wrap div,
    #footer-bottom-bar-wrap,
    #footer-bottom-bar-wrap div {
        background: var(--site-header-color);
    }

    #main-menu-container {
        background: var(--site-header-color) !important;
    }

    #SearchInput {
        background: var(--container-content);
        color: var(--font-color);
    }

    .search-button,
    .top-menu-list > a,
    .top-menu-list > a:hover {
        color: var(--font-color);
    }

    #top-menu-wrap,
    .top-menu-list,
    .top-partner-link,
    .top-partner-link:hover {
        background: var(--site-color) !important;
    }

    .top-menu-list,
    .top-menu-partner,
    .top-menu-partner:hover,
    #top-menu,
    #top-menu-social {
        border-left: 1px solid var(--site-header-color);
        border-right: 1px solid var(--site-header-color);
    }

    .dash-bar-menu a:hover {
        background: none;
    }

    #top-header .dash-bar-menu a i {
        margin-top: -3px;
    }

    /* Live Counts / Realtime Menu*/
    #top-menu-content-realtime > div > a,
    #top-menu-content-realtime > div > a:hover {
        color: var(--font-color);
    }

    #top-menu-content-realtime > div:first-child > h2 {
        color: var(--youtube-color);
    }

    #top-menu-content-realtime > div:nth-child(2) > h2 {
        color: var(--twitch-color);
    }

    #top-menu-content-realtime > div:nth-child(3) > h2 {
        color: var(--twitter-color);
    }

    #top-menu-content-realtime > div:nth-child(4) > h2 {
        color: var(--dailymotion-color);
    }

    #top-menu-content-realtime > div:nth-child(5) > h2 {
        color: var(--mixer-color);
    }

    /* Supported Platforms */
    #top-menu-content-charts > div > a,
    #top-menu-content-charts > div > a:hover {
        color: var(--font-color);
    }

    /* Top Lists Menu*/
    .top-menu-top-charts-content > a {
        color: var(--font-color);
    }

    .top-menu-category-button:hover {
        background: var(--container-highlight) !important;
    }

    .top-menu-category-button[data-rolechange="youtube"] {
        border-color: var(--youtube-color) !important;
    }

    .top-menu-category-button[data-rolechange="twitch"] {
        border-color: var(--twitch-color) !important;
    }

    .top-menu-category-button[data-rolechange="instagram"] {
        border-color: var(--instagram-color) !important;
    }

    .top-menu-category-button[data-rolechange="twitter"] {
        border-color: var(--twitter-color) !important;
    }

    .top-menu-category-button[data-rolechange="dailymotion"] {
        border-color: var(--dailymotion-color) !important;
    }

    .top-menu-category-button[data-rolechange="mixer"] {
        border-color: var(--mixer-color) !important;
    }

    .top-menu-category-button[data-rolechange="facebook"] {
        border-color: var(--facebook-color) !important;
    }

    .top-menu-category-button[data-rolechange="dlive"] {
        border-color: var(--dlive-color) !important;
    }

    #top-menu-content-youtube .top-menu-top-charts-header {
        color: var(--youtube-color);
    }

    #top-menu-content-twitch .top-menu-top-charts-header {
        color: var(--twitch-color) !important;
    }

    #top-menu-content-instagram .top-menu-top-charts-header {
        color: var(--instagram-color) !important;
    }

    #top-menu-content-twitter .top-menu-top-charts-header {
        color: var(--twitter-color) !important;
    }

    #top-menu-content-dailymotion .top-menu-top-charts-header {
        color: var(--dailymotion-color) !important;
    }

    #top-menu-content-mixer .top-menu-top-charts-header {
        color: var(--dailymotion-color) !important;
    }

    #top-menu-content-facebook .top-menu-top-charts-header {
        color: var(--facebook-color) !important;
    }

    #top-menu-content-dlive .top-menu-top-charts-header {
        color: var(--dlive-color) !important;
    }

    /* Buttons */
    .core-button {
        color: var(--font-color);
    }

    .OrangeButton,
    .FacebookButton,
    .TwitchButton,
    .TwitterButton,
    .GoogleButton,
    .SocialButton,
    .core-button,
    .core-button:hover {
        border: 1px solid var(--button-border-color) !important;
        border-radius: 0;
    }

    .OrangeButton,
    .SocialButton,
    .core-button,
    .core-button:hover {
        background: var(--site-color) !important;
    }

    .FacebookButton {
        background: var(--facebook-color);
    }

    .TwitchButton {
        background: var(--twitch-color);
    }

    .TwitterButton {
        background: var(--twitter-color);
    }

    .footer-social-block > div {
        border: 1px solid var(--button-border-color) !important;
    }

    .footer-social-block > div i {
        margin-top: 10px;
    }

    .footer-social-block[href*="youtube"] > div {
        background: var(--youtube-color) !important;
    }

    .footer-social-block[href*="facebook"] > div {
        background: var(--facebook-color) !important;
    }

    .footer-social-block[href*="twitter"] > div {
        background: var(--twitter-color) !important;
    }

    .footer-social-block[href*="instagram"] > div {
        background: var(--instagram-color) !important;
    }

    .core-button[href*="facebook.com"] {
        background: var(--facebook-color) !important;
    }

    .core-button[href*="twitch.com"] {
        background: var(--twitch-color) !important;
    }

    .core-button[href*="twitter.com"] {
        background: var(--twitter-color) !important;
    }

    .core-button[href*="instagram.com"] {
        background: var(--instagram-color) !important;
    }

    /* Search selection */
    .dd-options {
        border: none;
        width: 160px !important;
    }

    .dd-selected-text,
    .dd-option-text {
        cursor: pointer;
    }

    .dd-option {
        background: var(--container-background);
        color: var(--font-color);
        border: none;
    }

    .dd-option:hover {
        background: var(--container-highlight);
        color: var(--font-color);
    }

    .dd-selected {
        padding-top: 0;
    }

    .dd-select {
        border-radius: 0;
    }

    .dd-option-image {
        margin-top: 7px;
    }

    .dd-selected-image {
        margin-top: 3px;
    }

    .dd-option-selected {
        display: none;
    }

    /* Login Page */
    .dashboard-form-header {
        background: var(--container-background);
    }

    .dashboard-form-content {
        background: var(--container-content);
    }

    .dashboard-form {
        background: var(--container-highlight);
        border: none;
        color: var(--font-color);
    }

    /* Register Page */
    [for="dashboard_signup_newsletter"] + div {
        color: var(--font-color) !important;
    }

    /* User Tags*/
    .sb-tag-popular {
        background: var(--site-color);
    }

    /* Cookie Disclaimer */
    div[style="z-index: 9999999;"] div {
        background: var(--container-background);
        color: var(--font-color);
    }

    div[style="z-index: 9999999;"] a {
        color: var(--socialblade-color) !important;
    }

    /* Ads */
    iframe[id^="google_ads_iframe"] {
        display: /*[[hideAds]]*/;
    }

    div[id^="pa-container"] {
        display: /*[[hideAds]]*/
        !important;
    }

    #cdm-zone-06 {
        display: /*[[hideAdContainers]]*/;
    }

    /* Icons */
    img[src$="fav-youtube.png"],
    img[src$="fav-twitch.png"],
    img[src$="fav-twitter.png"],
    img[src$="fav-instagram.png"],
    img[src$="fav-facebook.png"] {
        display: none;
    }

    img[src$="fav-dailymotion.png"],
    img[src$="fav-mixer.png"],
    img[src$="fav-dlive.png"],
    img[src$="fav-storyfire.png"] {
        padding-top: 5px;
    }

    input + a > label::before {
        font: normal normal normal 14px FontAwesome;
        margin-right: 10px;
    }

    input ~ label::before {
        font: normal normal normal 14px FontAwesome;
        margin-right: 10px;
        margin-left: 5px;
    }

    #top-menu-content-charts a::before {
        font: normal normal normal 14px FontAwesome;
        margin-right: 8px;
    }

    input[value="youtube"] + a > label::before,
    input[value="youtube"] ~ label::before,
    #top-menu-content-charts a[href="/youtube"]::before {
        content: "\f16a";
        background: var(--youtube-color);
        padding: 3px 3px;
    }

    input[value="twitch"] + a > label::before,
    input[value="twitch"] ~ label::before,
    #top-menu-content-charts a[href="/twitch"]::before {
        content: "\f1e8";
        background: var(--twitch-color);
        padding: 3px 3px;
    }

    input[value="twitter"] + a > label::before,
    input[value="twitter"] ~ label::before,
    #top-menu-content-charts a[href="/twitter"]::before {
        content: "\f099";
        background: var(--twitter-color);
        padding: 3px 3.5px;
    }

    input[value="instagram"] + a > label::before,
    input[value="instagram"] ~ label::before,
    #top-menu-content-charts a[href="/instagram"]::before {
        content: "\f16d";
        background: var(--instagram-color);
        padding: 3px 4px;
    }

    input[value="facebook"] + a > label::before,
    input[value="facebook"] ~ label::before,
    #top-menu-content-charts a[href="/facebook"]::before {
        content: "\f09a";
        background: var(--facebook-color);
        padding: 3px 6px;
    }

    /* When SocialBlade updates their FontAwesome library*/
    /*
    input[value="dailymotion"] + a > label::before,
    input[value="dailymotion"] ~ label::before,
    #top-menu-content-charts a[href="/dailymotion"]::before {
        content: "\f952";
        background: var(--dailymotion-color);
        padding: 2px 3px 4px 3px;
    }

    input[value="mixer"] + a > label::before,
    input[value="mixer"] ~ label::before,
    #top-menu-content-charts a[href="/mixer"]::before {
        content: "\f956";
        background: var(--mixer-color);
        padding: 2px 3px 4px 3px;
    }
    */
    .dd-selected-text {
        margin-top: 0;
    }

    input.dd-selected-value + a > label.dd-selected-text::before {
        font-size: 16px;
    }

    input.dd-option-value ~ label.dd-option-text::before {
        font-size: 12px;
    }

    .global-notification {
        background: var(--site-color);
        border-bottom-color: var(--site-color);
        border-top: 1px solid var(--site-header-color);
    }

    /* Recent Blog Posts */
    .anchor-sidebar {
        background: var(--container-content);
        color: var(--font-color);
        border-top: 1px solid var(--container-background);
    }

    .anchor-sidebar:hover {
        background: var(--container-highlight);
    }

    /* Graphs */
    .highcharts-background,
    .highcharts-tooltip > path:last-of-type {
        fill: var(--container-content);
    }

    .highcharts-tooltip > path:last-of-type {
        opacity: 0.85;
    }

    .highcharts-yaxis-grid .highcharts-grid-line {
        stroke: var(--graph-line);
    }

    .highcharts-title,
    .highcharts-axis-title,
    .highcharts-axis-labels text,
    .highcharts-tooltip text {
        fill: var(--font-color) !important;
    }

    .highcharts-title > tspan,
    .highcharts-axis > text,
    .highcharts-axis-labels > text,
    .highcharts-tooltip > text {
        fill: var(--font-color) !important;
    }

    .highcharts-graph {
        stroke: var(--site-color);
    }

    .highcharts-markers > path {
        fill: var(--site-color)
    }

    .highcharts-label > path:last-of-type {
        stroke: var(--site-color);
        fill: var(--container-content);
        opacity: 0.85;
    }

    .highcharts-area {
        fill: var(--site-color);
        fill-opacity: 0.4;
    }

    .highcharts-tooltip > text > tspan:nth-of-type(2) {
        fill: var(--site-color) !important;
    }

    .highcharts-selection-marker {
        fill: var(--font-color) !important;
        fill-opacity: 0.25;
    }

    /* Graph Button */
    .highcharts-reset-zoom > rect {
        fill: var(--site-color);
    }

    .highcharts-reset-zoom > text {
        fill: var(--font-color) !important;
    }

    /* Favorite Feature Popup */
    #dialog_favorite_logincheck,
    .ui-dialog-titlebar-close,
    .ui-dialog-titlebar-close:hover {
        background: var(--container-content) !important;
    }

    .ui-dialog a {
        color: var(--site-color);
    }

    #dialog_favorite_logincheck,
    .ui-button-text {
        color: var(--font-color);
    }

    /*--------------------------------------------------*/
    /* 404 Page                                         */
    /*--------------------------------------------------*/
    .border-box {
        background: var(--container-background);
    }

    #error-404-symbol-container {
        background: var(--site-color)
    }

    #error-404-title-container h2 {
        color: var(--site-color);
    }

    #error-404-title-container p {
        color: var(--font-color);
    }

    /*--------------------------------------------------*/
    /* Cloudflare error                                 */
    /*--------------------------------------------------*/
    .cf-error-details ~ div h1,
    .cf-error-details ~ div p[style*="color:#55"] {
        color: var(--font-color) !important;
    }

    .cf-error-details ~ div:nth-child(4) {
        background: var(--container-background) !important;
    }

    * [style*="color:#7cca29"] {
        color: var(--good) !important;
    }

    * [style*="color:#b3382c"],
    * [style*="color:#52b9ff"] {
        color: var(--bad) !important;
    }
}

@-moz-document regexp("https?://socialblade.com/?") {
    /*--------------------------------------------------*/
    /* Home Page                                        */
    /*--------------------------------------------------*/
    #top-menu-wrap + div {
        border-bottom: 1px solid var(--site-color);
    }

    /* Search */
    input[name="query"] {
        background: var(--container-background) !important;
        border: none;
        color: var(--font-color);
        height: 60px !important;
    }

    form > button {
        background: var(--site-color) !important;
    }

    /* Numbers */
    .index-full[style*="#282828"] > div > div:first-child > p {
        color: var(--site-color) !important;
    }

    .index-full[style*="#282828"] > div > div:nth-child(2) > p {
        color: var(--youtube-color) !important;
    }

    .index-full[style*="#282828"] > div > div:nth-child(3) > p {
        color: var(--twitch-color) !important;
    }

    .index-full[style*="#282828"] > div > div:nth-child(4) > p {
        color: var(--twitter-color) !important;
    }

    .index-full[style*="#282828"] > div > div:nth-child(5) > p {
        color: var(--instagram-color) !important;
    }

    .index-full[style*="#282828"] > div > div:nth-child(6) > p {
        color: var(--facebook-color) !important;
    }

    .index-full[style*="#282828"] > div > div:nth-child(7) > p {
        color: var(--dailymotion-color) !important;
    }

    .index-full[style*="#282828"] > div > div:nth-child(8) > p {
        color: var(--mixer-color) !important;
    }

    /* Content */
    .index-full[style*="#fff"] {
        background: var(--container-background) !important;
    }

    .index-full[style*="#f4f4f4"],
    .index-full[style*="#282828"] {
        background: var(--site-background) !important;
    }

    p {
        color: var(--font-color) !important;
    }

    h2[style*="color:#222"],
    h3[style*="color:#333"],
    h3[style*="color:#444"] {
        color: var(--font-color) !important;
    }

    .index-full > div > a {
        color: var(--site-color);
    }

    #email {
        margin: 0px 10px !important;
        width: 230px !important;
        background: var(--site-background) !important;
        color: var(--font-color);
    }

    #submit {
        margin: 2px !important;
        padding: 10px 0px !important;
        width: 250px !important;
        border-radius: 0 !important;
        background: var(--site-color) !important;
    }
}

@-moz-document regexp("https?://socialblade.com/compare") {
    /*--------------------------------------------------*/
    /* Compare Main Page                                */
    /*--------------------------------------------------*/
    /* Buttons*/
    .compare-section-bottom {
        padding: 2.5px;
        margin-right: 25px;
    }

    .compare-anchor-button {
        border: 1px solid var(--site-background);
    }

    .compare-anchor-button[href^="/youtube"] {
        background: var(--youtube-color);
    }

    .compare-anchor-button[href^="/twitch"] {
        background: var(--twitch-color);
    }

    .compare-anchor-button[href^="/instagram"] {
        background: var(--instagram-color);
    }

    .compare-anchor-button[href^="/twitter"] {
        background: var(--twitter-color);
    }

    .compare-section-top {
        background: var(--container-content);
    }

    #about-wrap {
        background: var(--container-background);
    }

    #alpha-h1,
    #alpha-summary,
    .compare-h2,
    .compare-paragraph,
    .alpha-h2,
    .alpha-p,
    .alpha-h3 {
        color: var(--font-color);
    }

    .compare-paragraph {
        height: 168px;
    }
}

@-moz-document regexp("https?://socialblade.com/(youtube|twitch|twitter)/compare.*") {
    /*--------------------------------------------------*/
    /* Platform Compare Pages                           */
    /*--------------------------------------------------*/
    :root {
        --user1-color: #dd2323;
        --user2-color: #3289b2;
        --user3-color: #7a32b2;
        --user1-highlight: hsl(0, 73%, 58%);
        --user2-highlight: hsl(199, 56%, 53%);
        --user3-highlight: hsl(274, 56%, 53%);
    }    #about-wrap {
        background: var(--container-background);
    }

    #alpha-h1,
    .alpha-h2,
    .alpha-p,
    .alpha-h3 {
        color: var(--font-color);
    }

    #compareUser1,
    #compareUser2,
    #compareUser3 {
        background: var(--container-highlight);
    }

    #compareUser1,
    #compareUser2,
    #compareUser3 {
        border: none;
        color: var(--font-color) !important;
        margin-top: -3px;
    }

    button[id^="compare"][id$="Button-Submit"] {
        background: var(--site-color) !important;
        border-radius: 0 !important;
