        border-radius: 0;
    }

    div.share a.twitter,
    div.share a.twitter:hover {
        background: var(--twitter-color);
    }

    div.share a.facebook,
    div.share a.facebook:hover {
        background: var(--facebook-color);
    }

    div.share a.return,
    div.share a.return:hover {
        background: var(--site-color);
    }

    .page-realtime-body-containment .meta-twitter a span,
    .page-realtime-body-containment .meta-twitter a span:hover {
        background: var(--site-color);
    }

    /* Counter */
    div.page-realtime-body .containment {
        top: 53%;
    }

    .page-realtime-body h5.odometer {
        font-size: 10em;
    }

