    }

    /* Graph */
    .highcharts-axis-labels text {
        fill: var(--font-color) !important;
    }

    .highcharts-graph {
        stroke: var(--site-color);
    }

    .highcharts-markers > path {
        fill: var(--site-color)
    }

    .highcharts-label > path {
        stroke: var(--font-color)!important;
        fill: var(--site-background);
    }

    .highcharts-label > text {
        fill: var(--font-color) !important;
    }

    /* Change user */
    #changeLiveSettings,
    #changeLiveUser {
        background: var(--container-background);
        color: var(--font-color);
    }

    #changeLiveSettings input,
    #changeLiveSettings select,
    #changeLiveUser input {
        background: var(--container-background);
        color: var(--font-color);
    }
