        background: var(--container-content) !important;
    }

    #channel-management-page-1 > div[style^="width: 1180px"] > div {
        background: var(--container-content) !important;
    }

    #channel-management-page-1 > div[style^="width: 1180px"]:nth-of-type(4n + 3) > div {
        background: var(--container-highlight) !important;
    }

    #channel-management-page-1 > div[style^="width: 1180px"] > div {
        border-right: 1px solid var(--font-color) !important;
    }

    #channel-management-page-1 > div[style^="width: 1180px"] > div:last-child {
        border-right: none !important;
    }

    #channel-management-page-1 > div[style^="width: 1180px"] > div[style*="background: #fff"] {
        background: var(--container-background) !important;
        border-color: var(--container-background) !important;
    }

    #channel-management-page-2 > div[style^="width: 780px"] > div {
        background: var(--container-content) !important;
    }

    #management-ongoing-skype,
    #management-ongoing-youtubechannel,
    #management-ongoing-additional-notes {
        background: var(--container-content) !important;
        color: var(--font-color) !important;
    }
}

@-moz-document regexp("https?://socialblade.com/reports") {
    /*--------------------------------------------------*/
    /* SocialBlade Reporting                            */
    /*--------------------------------------------------*/
    #top-menu-wrap ~ div[style^="width: 1260px"] > div:nth-child(2) > div:nth-of-type(2) {
        background: var(--container-background) !important;
        color: var(--font-color) !important;
    }
}

@-moz-document regexp("https?://socialblade.com/reports/custom") {
    /*--------------------------------------------------*/
    /* SocialBlade Custom Reporting                     */
    /*--------------------------------------------------*/
    #top-menu-wrap ~ div[style^="width: 1260px"] > div[style^="width: 950px"] > div:nth-child(2) {
        background: var(--container-background) !important;
    }

    #top-menu-wrap ~ div[style^="width: 1260px"] > div[style^="width: 950px"] > div:nth-child(n + 3) {
        background: var(--container-content) !important;
    }

    #top-menu-wrap ~ div[style^="width: 1260px"] > div[style^="width: 950px"] > div:nth-child(n + 3) > div {
        color: var(--font-color) !important;
    }
}

@-moz-document regexp("https?://socialblade.com/reports/view/.*") {
    /*--------------------------------------------------*/
    /* SocialBlade Reporting View                       */
    /*--------------------------------------------------*/
    .body-header {
        background: var(--container-background);
        color: var(--font-color) !important;
    }

    .body-header > span > span {
        color: var(--site-color) !important;
    }

    .body-display > div {
        color: var(--font-color) !important;
    }

    .body-display > div:first-child {
        background: var(--container-background) !important;
    }

    .body-display > div:nth-child(n + 2) {
        background: var(--container-content) !important;
    }

    .body-display > div:nth-child(4n + 2) {
        background: var(--container-highlight) !important;
    }

    .body-display > div:last-child > div {
        color: var(--font-color) !important;
        background: var(--special) !important;
    }
}

@-moz-document regexp("https?://socialblade.com/account") {
    /*--------------------------------------------------*/
    /* SocialBlade Account Settings                     */
    /*--------------------------------------------------*/
    #top-menu-wrap ~ div[style^="width: 1260px"] > div[style*="background: #555"] {
        background: var(--container-background) !important;
    }

    #top-menu-wrap ~ div[style^="width: 1260px"] > div[style*="background: #fff"] {
        background: var(--container-content) !important;
    }

    input,
    select {
        background: var(--container-content) !important;
        color: var(--font-color) !important;
        border: 1px solid var(--font-color) !important;
    }

    #top-menu-wrap ~ div[style^="width: 1260px"] > .border-box[style*="background: #fff"] > div {
        background: none !important;
    }

    #account_geoloc_btn,
    #account_geoloc_btn_trash,
    #dashboard-displayname-set {
        border: 1px solid var(--font-color) !important;
        border-radius: 0 !important;
        margin-right: 2px;
        margin-top: 5px !important;
    }

    #account_geoloc_btn,
    #account_geoloc_btn_trash {
        height: 30px !important;
    }

    #dashboard-displayname-set {
        padding: 5px 22px 7px 22px !important;
    }

    .ui-knowledge,
    .ui-orange,
    .ui-widget-header {
        background: var(--site-color) !important;
    }

    .ui-black {
        background: var(--container-background);
    }

    .core-button {
        border-color: var(--font-color) !important;
    }

    .ui-widget-content {
        background: var(--container-content);
        border: 1px solid var(--font-color);
    }
}
