css:
@-moz-document domain("open.rocket.chat") {
:not(.sidebar-item__last-message) > .emoji.big {
	height: 200px !important;
  	width: 200px !important;
}

.avatar .avatar-image {
	border-radius: 50%;
}

button.user.user-card-message.color-primary-font-color {
	text-transform: capitalize;
}

button.user.user-card-message.color-primary-font-color span {
	text-transform: none;
}

svg.rc-icon[class*="icon--hashtag"] {
	transform: skewX(-12deg);
}

.rc-old .messages-container .footer {
	z-index: 3;
}

.messages-box .wrapper>ul {
	padding: 21px 0 0;
}

.messages-box .wrapper>ul::after {
	content: "";
	display: block;
	position: sticky;
	z-index: 1;
	bottom: -1px;
	height: 1.5rem;
	width: 100%;
	background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 255) 30%, rgba(255, 255, 255, 255) 100%);
}

.rc-message-box {
	margin-top: 0;
	padding-top: 0;
	padding-left: 0;
	padding-right: 0;
}

.rc-message-box__container {
	border: none;
	background-color: #fff;
	background-color: var(--header-background-color);
	box-shadow: 0 -1px 2px 0 rgba(31,35,41,.08);
}

.stream-info {
	padding: 24px 24px 0;
}

.rc-message-box__typing {
	top: -14px;
	margin-left: 16px;
	z-index: 2;
}

}
