height:96px;
width:96px;
}

.article-share-buttons {
-webkit-filter:grayscale(50%)!important;
filter:grayscale(50%)!important;
}

.actionBar-set.actionBar-set--external {
float: right;
margin-right: 10px;
}

.actionBar-set.actionBar-set--internal {
float: left;
margin-left: 10px;
}

.avatar.avatar--s {
