}

.article-meta {
color: #6b6b6b;
}

.entry-title {
text-align: center;     
}

.entry-content p {
padding-left: 5%;
}

.below_row_article_container {
width:100%;
justify-content: space-around;    
}

.menu {
background:#c8c8c8;
}

.site {
background:#CDCDCD;
width:100%;
}

.sub-menu {
color: #141414;
background: #eaeaea;
text-align: left;
border: 1px solid #fff;
box-shadow: 0 0 30px #333;
}

.the-post {
width:75%;
}

.top-bar {
background:#c2bebe;
width:100%;
}

.top-subscribe .subscribe-submit {
