}

.whatsapp-button {
background:linear-gradient(0deg,#9fbfba,#b2ead7)!important;
border-color:#eae8e8!important;
color:#000;
}

.wp-polls-form .Buttons,.show-comments,.follow-subscribe .subscribe-submit {
