border-color:#eae8e8!important;
color:#000;
}

.wp-polls .pollbar {
background: #000;
border: 1px solid #060606;
}

below_row_article_container .row_article {
width:30%;
}

.reactionsBar {
    background: 
#d5e8d570;
border: 1px solid
#dfdfdf;
    border-left-color: rgb(223, 223, 223);
    border-left-style: solid;
    border-left-width: 1px;
border-left-color: rgb(223, 223, 223);
border-left-style: solid;
border-left-width: 1px;
border-left: 0px solid #2d3339;
border-left-color:
    rgb(45, 51, 57);
    border-left-style: solid;
    border-left-width: 0px;
    filter: grayscale(10%) !important;
}

.avatar {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    border-radius: 0% !important;
    vertical-align: top;
    overflow: hidden;
    -webkit-filter: saturate(0%) !important; 
}

